import{_ as i,D as t,c as a,I as r,l as e,a as o,a5 as n,o as l}from"./chunks/framework.D561hJ3q.js";const P=JSON.parse(`{"title":"Attaques d'applications","description":"","frontmatter":{"sidebar":"auto"},"headers":[],"relativePath":"certification/securityplus/concepts/app-att.md","filePath":"certification/securityplus/concepts/app-att.md","lastUpdated":1711477170000}`),u={name:"certification/securityplus/concepts/app-att.md"},c=e("h1",{id:"attaques-d-applications",tabindex:"-1"},[o("Attaques d'applications "),e("a",{class:"header-anchor",href:"#attaques-d-applications","aria-label":`Permalink to "Attaques d'applications"`},"​")],-1),d=n('<h2 id="implementation-de-protocoles-securises-objectif-3-1" tabindex="-1">Implémentation de protocoles sécurisés (Objectif 3.1) <a class="header-anchor" href="#implementation-de-protocoles-securises-objectif-3-1" aria-label="Permalink to &quot;Implémentation de protocoles sécurisés (Objectif 3.1)&quot;">​</a></h2><h4 id="voix-et-video" tabindex="-1">Voix et vidéo : <a class="header-anchor" href="#voix-et-video" aria-label="Permalink to &quot;Voix et vidéo :&quot;">​</a></h4><ol><li><em><strong>Secure Real-Time Transport Protocol</strong></em> (SRTP) : Assure la sécurité des communications audio et vidéo.</li></ol><h4 id="synchronisation-du-temps" tabindex="-1">Synchronisation du temps : <a class="header-anchor" href="#synchronisation-du-temps" aria-label="Permalink to &quot;Synchronisation du temps :&quot;">​</a></h4><ol><li><em><strong>Network Time Protocol</strong></em> (NTP) : Maintient la synchronisation temporelle entre les appareils pour une corrélation efficace des journaux.</li></ol><h4 id="email" tabindex="-1">Email : <a class="header-anchor" href="#email" aria-label="Permalink to &quot;Email :&quot;">​</a></h4><ol><li><em><strong>Simple Mail Transfer Protocol</strong></em> (SMTP) : Transfère les messages entre serveurs de messagerie.</li><li><em><strong>SMTPS</strong></em> (SMTP Secure) : Version sécurisée de SMTP pour des connexions sécurisées.</li><li><em><strong>Post Office Protocol version 3</strong></em> (POP3) : Protocole de récupération des emails pour les utilisateurs finaux.</li><li><em><strong>POP3 over SSL</strong></em> / <em><strong>Internet Message Access Protocol</strong></em> (IMAP) : Versions sécurisées de POP3 et IMAP.</li><li><em><strong>IMAP Secure</strong></em> (IMAPS) : Protocole sécurisé pour récupérer des emails.</li><li><em><strong>Secure Multipurpose Internet Mail Extensions</strong></em> (SMIME) : Fournit un chiffrement de bout en bout pour les messages électroniques.</li></ol><h4 id="navigateur-web" tabindex="-1">Navigateur web : <a class="header-anchor" href="#navigateur-web" aria-label="Permalink to &quot;Navigateur web :&quot;">​</a></h4><ol><li><em><strong>Hypertext Transfer Protocol Secure</strong></em> (HTTPS) : Version sécurisée du protocole HTTP pour les sites web.</li></ol><h4 id="transfert-de-fichiers" tabindex="-1">Transfert de fichiers : <a class="header-anchor" href="#transfert-de-fichiers" aria-label="Permalink to &quot;Transfert de fichiers :&quot;">​</a></h4><ol><li><em><strong>File Transfer Protocol Secure</strong></em> (FTPS) : Protocole sécurisé pour le transfert de fichiers.</li><li><em><strong>SSH File Transfer Protocol</strong></em> (SFTP) : Protocole sécurisé de transfert de fichiers via SSH.</li></ol><h4 id="services-d-annuaire" tabindex="-1">Services d&#39;annuaire : <a class="header-anchor" href="#services-d-annuaire" aria-label="Permalink to &quot;Services d&#39;annuaire :&quot;">​</a></h4><ol><li><em><strong>Lightweight Directory Access Protocol Secure</strong></em> (LDAPS) : Version sécurisée de LDAP pour l&#39;accès aux annuaires.</li></ol><h4 id="connexions-administratives" tabindex="-1">Connexions administratives : <a class="header-anchor" href="#connexions-administratives" aria-label="Permalink to &quot;Connexions administratives :&quot;">​</a></h4><ol><li><em><strong>Secure Shell</strong></em> (SSH) : Protocole assurant des connexions administratives sécurisées aux serveurs distants.</li></ol><h4 id="reseaux-prives-virtuels-vpn" tabindex="-1">Réseaux privés virtuels (VPN) : <a class="header-anchor" href="#reseaux-prives-virtuels-vpn" aria-label="Permalink to &quot;Réseaux privés virtuels (VPN) :&quot;">​</a></h4><ol><li><em><strong>Internet Protocol Secure</strong></em> (IPsec) : Protocole assurant l&#39;authentification et la confidentialité des données dans les tunnels VPN.</li><li><em><strong>Authentication Header</strong></em> (AH) : Protocole assurant l&#39;authentification et la confidentialité des données dans les tunnels VPN.</li><li><em><strong>Encapsulating Security Payload</strong></em> (ESP) : Protocole assurant l&#39;authentification et la confidentialité des données dans les tunnels VPN.</li></ol><h4 id="resolution-de-noms-de-domaine" tabindex="-1">Résolution de noms de domaine : <a class="header-anchor" href="#resolution-de-noms-de-domaine" aria-label="Permalink to &quot;Résolution de noms de domaine :&quot;">​</a></h4><ol><li><em><strong>DNS Security Extensions</strong></em> (DNSSEC) : Ajoute une authentification forte aux requêtes DNS pour prévenir les attaques de falsification.</li></ol><h4 id="gestion-des-reseaux" tabindex="-1">Gestion des réseaux : <a class="header-anchor" href="#gestion-des-reseaux" aria-label="Permalink to &quot;Gestion des réseaux :&quot;">​</a></h4><ol><li><em><strong>Simple Network Management Protocol version 3</strong></em> (SNMPv3) : Version sécurisée de SNMP pour la gestion des équipements réseau.</li></ol><h4 id="allocation-d-adresses-ip" tabindex="-1">Allocation d&#39;adresses IP : <a class="header-anchor" href="#allocation-d-adresses-ip" aria-label="Permalink to &quot;Allocation d&#39;adresses IP :&quot;">​</a></h4><ol><li><em><strong>Dynamic Host Configuration Protocol</strong></em> (DHCP) : Protocole principal pour l&#39;allocation dynamique des adresses IP sur un réseau local.</li></ol><p>Ces protocoles sont essentiels pour garantir la sécurité des communications, des transferts de données et de la gestion des réseaux dans les environnements informatiques. Maîtriser leur utilisation et leur mise en œuvre est crucial pour réussir l&#39;examen Security+.</p><h2 id="solutions-de-securite-pour-les-hotes-et-les-applications-objectif-3-2" tabindex="-1">Solutions de sécurité pour les hôtes et les applications (Objectif 3.2) <a class="header-anchor" href="#solutions-de-securite-pour-les-hotes-et-les-applications-objectif-3-2" aria-label="Permalink to &quot;Solutions de sécurité pour les hôtes et les applications (Objectif 3.2)&quot;">​</a></h2><p>Implémentation de solutions de sécurité pour les hôtes ou les applications.</p><h3 id="protection-des-points-d-extremite" tabindex="-1">Protection des points d&#39;extrémité : <a class="header-anchor" href="#protection-des-points-d-extremite" aria-label="Permalink to &quot;Protection des points d&#39;extrémité :&quot;">​</a></h3><ol><li>Utilisation d&#39;antivirus et d&#39;anti-malwares standards pour protéger les points d&#39;extrémité contre les logiciels malveillants.</li><li>Solutions de détection et réponse aux incidents sur les points d&#39;extrémité (EDR) pour gérer la mise en quarantaine et la suppression de codes malveillants.</li><li>Prévention de la perte de données (DLP) pour surveiller et bloquer les tentatives de déplacement de données sensibles en dehors de l&#39;organisation.</li><li>Pare-feu nouvelle génération (NGFW) pour bloquer les connexions réseau non désirées.</li></ol><h3 id="detection-et-prevention-des-intrusions" tabindex="-1">Détection et prévention des intrusions : <a class="header-anchor" href="#detection-et-prevention-des-intrusions" aria-label="Permalink to &quot;Détection et prévention des intrusions :&quot;">​</a></h3><ol><li>Systèmes de détection d&#39;intrusion (IDS) surveillant le trafic entrant pour détecter des activités malveillantes.</li><li>Systèmes de prévention d&#39;intrusion (IPS) bloquant les connexions potentiellement malveillantes.</li></ol><h3 id="integrite-du-demarrage" tabindex="-1">Intégrité du démarrage : <a class="header-anchor" href="#integrite-du-demarrage" aria-label="Permalink to &quot;Intégrité du démarrage :&quot;">​</a></h3><ol><li>Utilisation de l&#39;interface firmware extensible et unifiée (UEFI) pour sécuriser le processus de démarrage des systèmes modernes.</li></ol><h3 id="securite-des-bases-de-donnees" tabindex="-1">Sécurité des bases de données : <a class="header-anchor" href="#securite-des-bases-de-donnees" aria-label="Permalink to &quot;Sécurité des bases de données :&quot;">​</a></h3><ol><li>Identification et suppression d&#39;informations sensibles inutiles dans la base de données.</li><li>Utilisation de techniques comme le hachage et la tokenisation pour transformer les données sensibles en versions non sensibles.</li></ol><h3 id="securite-des-applications" tabindex="-1">Sécurité des applications : <a class="header-anchor" href="#securite-des-applications" aria-label="Permalink to &quot;Sécurité des applications :&quot;">​</a></h3><ol><li>Validation des entrées pour détecter tout contenu potentiellement malveillant.</li><li>Gestion des applications via une liste blanche ou une liste noire.</li><li>Signature de code pour attester de la légitimité de la source du code.</li><li>Pratiques de codage sécurisé et tests de sécurité, incluant l&#39;analyse statique et dynamique du code.</li></ol><h3 id="durcissement-des-systemes" tabindex="-1">Durcissement des systèmes : <a class="header-anchor" href="#durcissement-des-systemes" aria-label="Permalink to &quot;Durcissement des systèmes :&quot;">​</a></h3><ol><li>Suppression des ports ouverts et des services inutiles.</li><li>Configuration sécurisée du système d&#39;exploitation.</li><li>Gestion régulière des correctifs de sécurité.</li></ol><h3 id="securite-materielle" tabindex="-1">Sécurité matérielle : <a class="header-anchor" href="#securite-materielle" aria-label="Permalink to &quot;Sécurité matérielle :&quot;">​</a></h3><ol><li>Utilisation du module de plateforme sécurisée (TPM) pour gérer les clés de chiffrement et établir une chaîne de confiance matérielle.</li></ol><p>Ces solutions sont essentielles pour assurer la sécurité des hôtes et des applications dans un environnement informatique. Il est important de comprendre leur mise en œuvre pour réussir l&#39;examen Security Plus.</p><h2 id="conception-de-reseaux-securises-objectif-3-3" tabindex="-1">Conception de réseaux sécurisés (Objectif 3.3) <a class="header-anchor" href="#conception-de-reseaux-securises-objectif-3-3" aria-label="Permalink to &quot;Conception de réseaux sécurisés (Objectif 3.3)&quot;">​</a></h2><p>Implémentation de conceptions de réseaux sécurisées</p><h3 id="equilibrage-de-charge-load-balancing" tabindex="-1">Équilibrage de charge (Load Balancing) <a class="header-anchor" href="#equilibrage-de-charge-load-balancing" aria-label="Permalink to &quot;Équilibrage de charge (Load Balancing)&quot;">​</a></h3><ol><li>Distribution des tâches sur plusieurs serveurs pour la résilience et la sécurité.</li><li>Mode actif-actif ou actif-passif pour une disponibilité accrue.</li></ol><h3 id="segmentation-reseau-network-segmentation" tabindex="-1">Segmentation réseau (Network Segmentation) <a class="header-anchor" href="#segmentation-reseau-network-segmentation" aria-label="Permalink to &quot;Segmentation réseau (Network Segmentation)&quot;">​</a></h3><ol><li>Utilisation de VLAN pour regrouper les systèmes avec des niveaux de sécurité similaires.</li><li>Utilisation de pare-feu pour séparer l&#39;internet de l&#39;intranet et pour créer une zone démilitarisée (DMZ).</li></ol><h3 id="reseaux-prives-virtuels-vpn-1" tabindex="-1">Réseaux privés virtuels (VPN) <a class="header-anchor" href="#reseaux-prives-virtuels-vpn-1" aria-label="Permalink to &quot;Réseaux privés virtuels (VPN)&quot;">​</a></h3><ol><li>Fourniture d&#39;un accès distant sécurisé et connexion des sites distants.</li><li>Protocoles principaux : IPsec, TLS, HTML 5, et L2TP.</li></ol><h3 id="controle-d-acces-reseau-network-access-control-nac" tabindex="-1">Contrôle d&#39;accès réseau (Network Access Control - NAC) <a class="header-anchor" href="#controle-d-acces-reseau-network-access-control-nac" aria-label="Permalink to &quot;Contrôle d&#39;accès réseau (Network Access Control - NAC)&quot;">​</a></h3><ol><li>Authentification des dispositifs avant la connexion au réseau.</li><li>Vérification de la posture du dispositif pour garantir une configuration sécurisée.</li></ol><h3 id="securite-des-ports-port-security" tabindex="-1">Sécurité des ports (Port Security) <a class="header-anchor" href="#securite-des-ports-port-security" aria-label="Permalink to &quot;Sécurité des ports (Port Security)&quot;">​</a></h3><ol><li>Vérification des adresses MAC des systèmes sur le réseau.</li><li>Enregistrement de l&#39;adresse MAC sur le port du commutateur pour un accès autorisé.</li></ol><h3 id="serveurs-de-saut-jump-servers" tabindex="-1">Serveurs de saut (Jump Servers) <a class="header-anchor" href="#serveurs-de-saut-jump-servers" aria-label="Permalink to &quot;Serveurs de saut (Jump Servers)&quot;">​</a></h3><ol><li>Fournissent un accès sécurisé entre les réseaux pour les administrateurs.</li></ol><h3 id="serveurs-mandataires-proxy-servers" tabindex="-1">Serveurs mandataires (Proxy Servers) <a class="header-anchor" href="#serveurs-mandataires-proxy-servers" aria-label="Permalink to &quot;Serveurs mandataires (Proxy Servers)&quot;">​</a></h3><ol><li>Négocient les connexions réseau pour les utilisateurs internes.</li><li>Effectuent un filtrage de contenu pour éviter les activités malveillantes.</li></ol><h3 id="systemes-de-detection-et-prevention-des-intrusions-ids-ips" tabindex="-1">Systèmes de détection et prévention des intrusions (IDS/IPS) <a class="header-anchor" href="#systemes-de-detection-et-prevention-des-intrusions-ids-ips" aria-label="Permalink to &quot;Systèmes de détection et prévention des intrusions (IDS/IPS)&quot;">​</a></h3><ol><li>Surveillent les activités suspectes sur le réseau.</li><li>Les IDS alertent les administrateurs tandis que les IPS bloquent le trafic malveillant.</li></ol><h3 id="pare-feu-firewalls" tabindex="-1">Pare-feu (Firewalls) <a class="header-anchor" href="#pare-feu-firewalls" aria-label="Permalink to &quot;Pare-feu (Firewalls)&quot;">​</a></h3><ol><li>Stateless, Stateful, et Next-Generation Firewalls protègent le réseau contre les trafics non autorisés.</li><li>Pare-feu d&#39;applications web protègent les applications web contre les vulnérabilités.</li></ol><h3 id="surveillance-de-l-integrite-des-fichiers-file-integrity-monitoring" tabindex="-1">Surveillance de l&#39;intégrité des fichiers (File Integrity Monitoring) <a class="header-anchor" href="#surveillance-de-l-integrite-des-fichiers-file-integrity-monitoring" aria-label="Permalink to &quot;Surveillance de l&#39;intégrité des fichiers (File Integrity Monitoring)&quot;">​</a></h3><ol><li>Surveille les modifications non autorisées des fichiers et alerte les administrateurs.</li></ol><p>Ces éléments sont essentiels pour concevoir des réseaux sécurisés et protéger les données et les systèmes contre les menaces. Comprendre leur mise en œuvre est crucial pour réussir l&#39;examen Security+.</p><h2 id="installation-et-configuration-des-parametres-de-securite-sans-fil-objectif-3-4" tabindex="-1">Installation et configuration des paramètres de sécurité sans fil (Objectif 3.4) <a class="header-anchor" href="#installation-et-configuration-des-parametres-de-securite-sans-fil-objectif-3-4" aria-label="Permalink to &quot;Installation et configuration des paramètres de sécurité sans fil (Objectif 3.4)&quot;">​</a></h2><h3 id="protocoles-cryptographiques" tabindex="-1">Protocoles cryptographiques <a class="header-anchor" href="#protocoles-cryptographiques" aria-label="Permalink to &quot;Protocoles cryptographiques&quot;">​</a></h3><ol><li><em><strong>Wired Equivalent Privacy</strong></em> (WEP) : Considéré comme obsolète et non sécurisé.</li><li><em><strong>Wifi Protected Access</strong></em> (WPA) : Protocole initialement sécurisé, mais remplacé par WPA2 et WPA3.</li><li><em><strong>WPA2</strong></em> : Utilise CCMP (Counter Mode Cipher Block Chaining Message Authentication Protocol) avec l&#39;Advanced Encryption Standard (AES).</li><li><em><strong>WPA3</strong></em> : Plus sécurisé que WPA2, utilise SAE (Simultaneous Authentication of Equals) basé sur l&#39;algorithme de Diffie-Hellman.</li></ol><h3 id="methodes-d-authentification" tabindex="-1">Méthodes d&#39;authentification <a class="header-anchor" href="#methodes-d-authentification" aria-label="Permalink to &quot;Méthodes d&#39;authentification&quot;">​</a></h3><ol><li><em><strong>Clé pré-partagée</strong></em> (PSK) : Facile à mettre en œuvre mais nécessite la reconfiguration des appareils lors du changement de mot de passe.</li><li><em><strong>Mode entreprise</strong></em> : Utilisation des identifiants de l&#39;utilisateur pour l&#39;accès au réseau, avec authentification via le protocole <strong>IEEE 802.1x</strong> et <strong>EAP</strong> (<em>Extensible Authentication Protocol</em>).</li></ol><h3 id="authentification-pour-utilisateurs-inconnus" tabindex="-1">Authentification pour utilisateurs inconnus <a class="header-anchor" href="#authentification-pour-utilisateurs-inconnus" aria-label="Permalink to &quot;Authentification pour utilisateurs inconnus&quot;">​</a></h3><ol><li><em><strong>Réseau ouvert</strong></em> : Permet à quiconque de se connecter sans authentification.</li><li><em><strong>Portail captif</strong></em> : Page web demandant une authentification avant d&#39;accéder au réseau.</li></ol><h3 id="considerations-d-installation" tabindex="-1">Considérations d&#39;installation <a class="header-anchor" href="#considerations-d-installation" aria-label="Permalink to &quot;Considérations d&#39;installation&quot;">​</a></h3><ol><li><em><strong>Environnement physique</strong></em> : Étudier l&#39;environnement pour optimiser la disposition des points d&#39;accès sans fil et minimiser les interférences.</li><li><em><strong>Étude de site</strong></em> : Effectuer une étude pour comprendre la propagation des ondes radio et placer les équipements de manière appropriée.</li><li><em><strong>Analyse wifi</strong></em> : Utiliser un analyseur Wifi pour évaluer la couverture réseau et produire une carte de chaleur pour visualiser la force du signal.</li></ol><p>Ces paramètres sont cruciaux pour assurer la sécurité des réseaux sans fil et minimiser les risques d&#39;accès non autorisés. Une compréhension approfondie est essentielle pour réussir l&#39;examen Security Plus.</p><h2 id="implementation-de-solutions-sobiles-securisees-objectif-3-5" tabindex="-1">Implémentation de solutions sobiles sécurisées (Objectif 3.5) <a class="header-anchor" href="#implementation-de-solutions-sobiles-securisees-objectif-3-5" aria-label="Permalink to &quot;Implémentation de solutions sobiles sécurisées (Objectif 3.5)&quot;">​</a></h2><h3 id="methodes-de-connexion-mobiles" tabindex="-1">Méthodes de connexion mobiles <a class="header-anchor" href="#methodes-de-connexion-mobiles" aria-label="Permalink to &quot;Méthodes de connexion mobiles&quot;">​</a></h3><ol><li><em><strong>Réseaux cellulaires et Wifi</strong></em> : Connectivité à large bande pour accéder à Internet.</li><li><em><strong>Bluetooth</strong></em> : Pour connecter des casques sans fil, des ordinateurs et d&#39;autres appareils.</li><li><em><strong>Near-Field Communication</strong></em> (NFC) : Pour les paiements sans contact.</li><li><em><strong>Réseaux infrarouges et câbles USB</strong></em> : Partage de données entre appareils mobiles et systèmes.</li></ol><h3 id="protocoles-specialises" tabindex="-1">Protocoles spécialisés <a class="header-anchor" href="#protocoles-specialises" aria-label="Permalink to &quot;Protocoles spécialisés&quot;">​</a></h3><ol><li><em><strong>Système de positionnement global</strong></em> (GPS) : Utilisation de satellites pour localiser les appareils.</li><li><em><strong>Technologie de radio-identification</strong></em> (RFID) : Pour suivre les appareils dans des zones restreintes.</li></ol><h3 id="gestion-des-appareils-mobiles-mdm" tabindex="-1">Gestion des appareils mobiles (MDM) <a class="header-anchor" href="#gestion-des-appareils-mobiles-mdm" aria-label="Permalink to &quot;Gestion des appareils mobiles (MDM)&quot;">​</a></h3><ol><li>Utilisation de logiciels MDM pour maintenir les appareils mobiles en configuration sécurisée.</li><li>Gestion des applications installées, filtrage du contenu et conformité aux politiques de sécurité.</li><li>Possibilité de suppression à distance des appareils et délimitation géographique.</li></ol><h3 id="securite-des-appareils-mobiles" tabindex="-1">Sécurité des appareils mobiles <a class="header-anchor" href="#securite-des-appareils-mobiles" aria-label="Permalink to &quot;Sécurité des appareils mobiles&quot;">​</a></h3><ol><li>Enforcement de politiques de sécurité comme le verrouillage d&#39;écran, les mots de passe et l&#39;encryption des données.</li><li>Utilisation de modules de sécurité matérielle pour gérer les clés de chiffrement.</li><li>Mise en œuvre de systèmes d&#39;exploitation sécurisés comme SE Android pour les applications hautement sécurisées.</li></ol><h3 id="politiques-de-securite-mobile" tabindex="-1">Politiques de sécurité mobile <a class="header-anchor" href="#politiques-de-securite-mobile" aria-label="Permalink to &quot;Politiques de sécurité mobile&quot;">​</a></h3><ol><li>Gestion des applications installées et des mises à jour.</li><li>Limitation de l&#39;utilisation de boutiques d&#39;applications tierces et du jailbreak.</li><li>Contrôle des fonctionnalités telles que l&#39;appareil photo, les services de messagerie et le réseau.</li><li>Déploiement selon quatre modèles : appareils détenus par l&#39;entreprise, choix de l&#39;appareil, appareils d&#39;entreprise avec usage personnel et appareils personnels utilisés pour l&#39;entreprise.</li></ol><p>En comprenant et en appliquant ces concepts, vous pouvez garantir la sécurité des solutions mobiles dans votre organisation. Ces connaissances sont essentielles pour réussir l&#39;examen Security+.</p><h2 id="application-de-solutions-de-cybersecurite-dans-le-cloud-objectif-3-6" tabindex="-1">Application de solutions de cybersécurité dans le Cloud (Objectif 3.6) <a class="header-anchor" href="#application-de-solutions-de-cybersecurite-dans-le-cloud-objectif-3-6" aria-label="Permalink to &quot;Application de solutions de cybersécurité dans le Cloud (Objectif 3.6)&quot;">​</a></h2><p>En développant des solutions cloud sécurisées, plusieurs considérations clés doivent être prises en compte :</p><h3 id="conception-de-solutions-cloud" tabindex="-1">Conception de solutions Cloud <a class="header-anchor" href="#conception-de-solutions-cloud" aria-label="Permalink to &quot;Conception de solutions Cloud&quot;">​</a></h3><ol><li>Utiliser différentes zones de service offertes par le fournisseur pour une haute disponibilité et une résilience accrue.</li><li>Élaborer des politiques de ressources pour limiter les actions des utilisateurs et réduire l&#39;exposition de l&#39;organisation.</li></ol><h3 id="selection-du-fournisseur-cloud" tabindex="-1">Sélection du fournisseur Cloud <a class="header-anchor" href="#selection-du-fournisseur-cloud" aria-label="Permalink to &quot;Sélection du fournisseur Cloud&quot;">​</a></h3><ol><li>Évaluer les intégrations disponibles avec les technologies existantes et les capacités d&#39;audit pour maintenir les politiques de sécurité.</li><li>Considérer les contrôles de sécurité spécifiques au service, tels que la gestion des permissions de stockage, le cryptage des données et la réplication pour la protection des données.</li></ol><h3 id="utilisation-des-ressources-reseau" tabindex="-1">Utilisation des ressources réseau <a class="header-anchor" href="#utilisation-des-ressources-reseau" aria-label="Permalink to &quot;Utilisation des ressources réseau&quot;">​</a></h3><ol><li>Créer des réseaux virtuels privés pour segmenter les ressources cloud et fournir une isolation similaire à celle d&#39;un centre de données sur site.</li><li>Utiliser des groupes de sécurité pour contrôler l&#39;accès aux instances de calcul, agissant comme des règles de pare-feu dans le cloud.</li></ol><h3 id="surveillance-et-gestion-des-ressources-cloud" tabindex="-1">Surveillance et gestion des ressources Cloud <a class="header-anchor" href="#surveillance-et-gestion-des-ressources-cloud" aria-label="Permalink to &quot;Surveillance et gestion des ressources Cloud&quot;">​</a></h3><ol><li>Surveiller et gérer dynamiquement les ressources allouées pour éviter les instances inutilisées et les coûts inutiles.</li><li>Utiliser des solutions spécifiques au cloud telles que les courtiers d&#39;accès cloud (CASB) pour appliquer les politiques de sécurité et les passerelles web sécurisées pour filtrer les demandes des utilisateurs.</li></ol><h3 id="evaluation-des-solutions-cloud" tabindex="-1">Évaluation des solutions Cloud <a class="header-anchor" href="#evaluation-des-solutions-cloud" aria-label="Permalink to &quot;Évaluation des solutions Cloud&quot;">​</a></h3><ol><li>Examiner les contrôles de sécurité natifs du fournisseur cloud ainsi que les solutions tierces en fonction des critères de coût et de fonctionnalité.</li></ol><p>En comprenant et en appliquant ces concepts, vous pouvez renforcer la posture de sécurité de votre organisation dans le cloud. Ces connaissances sont essentielles pour réussir l&#39;examen Security Plus.</p><h2 id="mise-en-œuvre-des-controles-de-gestion-des-identites-et-des-comptes-objectif-3-7" tabindex="-1">Mise en œuvre des contrôles de gestion des identités et des comptes (Objectif 3.7) <a class="header-anchor" href="#mise-en-œuvre-des-controles-de-gestion-des-identites-et-des-comptes-objectif-3-7" aria-label="Permalink to &quot;Mise en œuvre des contrôles de gestion des identités et des comptes (Objectif 3.7)&quot;">​</a></h2><p>La gestion des identités constitue l&#39;un des éléments fondamentaux d&#39;un programme de sécurité. Sans la capacité d&#39;identifier et d&#39;authentifier les utilisateurs, il est impossible de prendre des décisions de sécurité éclairées. Les solutions de gestion des identités et des accès (<strong>IAM</strong>) reposent sur les concepts de sujets et d&#39;objets. Les sujets sont les personnes, les systèmes ou les services qui souhaitent accéder à des ressources, tandis que les objets sont les ressources auxquelles ils souhaitent accéder.</p><p>Chaque sujet ayant une identité dans le système de gestion des identités et des accès possède plusieurs attributs associés à son identité. Ces attributs peuvent inclure des éléments tels que leur rôle professionnel, leur affiliation à l&#39;organisation, le département dans lequel ils se trouvent, ou d&#39;autres caractéristiques liées à leur identité.</p><p>Le fournisseur d&#39;identité, ou IdP, est l&#39;organisation qui fournit cette identité numérique à un utilisateur. Les IdP sont généralement l&#39;employeur d&#39;un individu, son école ou une organisation similaire. Les utilisateurs peuvent prouver leur identité par diverses techniques, telles que l&#39;utilisation de certificats numériques, de jetons de sécurité matériels ou logiciels, de cartes à puce ou de clés SSH.</p><p>En mettant en place une solution de gestion des comptes, vous devez être conscient des différents types de comptes qui peuvent exister :</p><ol><li>Les comptes utilisateur standard et les comptes super-utilisateur avec des privilèges administratifs.</li><li>Les comptes de service utilisés par les systèmes d&#39;exploitation et les applications pour accéder aux ressources, mais non destinés à une connexion interactive par des personnes.</li></ol><p>Vous devez éviter les comptes partagés entre plusieurs personnes, ainsi que les comptes d&#39;invités ou de fournisseurs. Cela viole le principe de responsabilité, car les actions effectuées avec ces comptes ne peuvent pas être attribuées à un individu spécifique.</p><p>Dans le cadre de votre programme de gestion des identités et des accès, vous devriez également établir plusieurs politiques de compte, notamment en ce qui concerne les mots de passe :</p><ul><li>Expiration des mots de passe, réutilisation des anciens mots de passe, complexité des mots de passe.</li></ul><p>Les systèmes de gestion des identités peuvent également prendre en compte d&#39;autres facteurs tels que l&#39;adresse IP de l&#39;utilisateur, la géolocalisation obtenue à partir des données GPS, ou encore les horaires de connexion pour restreindre l&#39;accès aux systèmes.</p><p>Ces politiques vous permettent de créer un environnement solide de gestion des identités et des accès, où vous pouvez verrouiller et désactiver les comptes en cas d&#39;activité suspecte, ainsi que d&#39;effectuer des audits réguliers pour garantir que l&#39;activité des utilisateurs correspond à vos attentes. Ces connaissances sont essentielles pour réussir l&#39;examen Security Plus.</p><h2 id="mettre-en-œuvre-des-solutions-d-authentification-et-d-autorisation-objectif-3-8" tabindex="-1">Mettre en œuvre des solutions d&#39;authentification et d&#39;autorisation (Objectif 3.8) <a class="header-anchor" href="#mettre-en-œuvre-des-solutions-d-authentification-et-d-autorisation-objectif-3-8" aria-label="Permalink to &quot;Mettre en œuvre des solutions d&#39;authentification et d&#39;autorisation (Objectif 3.8)&quot;">​</a></h2><p>Protéger les informations d&#39;identification est crucial, et des technologies telles que les coffres-forts de mots de passe, les TPM (Trusted Platform Modules) et les HSM (Hardware Security Modules) aident à protéger les mots de passe et les clés d&#39;accès.</p><h3 id="technologies-d-authentification-et-d-authorisation" tabindex="-1">Technologies d&#39;Authentification et d&#39;Authorisation : <a class="header-anchor" href="#technologies-d-authentification-et-d-authorisation" aria-label="Permalink to &quot;Technologies d&#39;Authentification et d&#39;Authorisation :&quot;">​</a></h3><ol><li><em><strong>Authentification unique</strong></em> (SSO) : Permet aux utilisateurs de s&#39;authentifier une seule fois et d&#39;accéder à plusieurs systèmes. Kerberos est une solution centralisée à cet effet.</li><li><em><strong>Protocoles obsolètes</strong></em> : Le Protocole d&#39;Authentification par Mot de Passe (PAP) et le Protocole d&#39;Authentification par Poignée de Main (CHAP) doivent être évités en raison de vulnérabilités de sécurité.</li><li><em><strong>Solutions d&#39;authentification fédérée</strong></em> : SAML, OpenID Connect et OAuth permettent l&#39;authentification à travers des organisations et des services.</li></ol><h3 id="modeles-de-controle-d-acces" tabindex="-1">Modèles de contrôle d&#39;accès : <a class="header-anchor" href="#modeles-de-controle-d-acces" aria-label="Permalink to &quot;Modèles de contrôle d&#39;accès :&quot;">​</a></h3><ol><li><em><strong>Contrôle d&#39;accès obligatoire</strong></em> (MAC) : Applique les exigences de sécurité sans exception en se basant sur des niveaux de sécurité.</li><li><em><strong>Contrôle d&#39;accès discrétionnaire</strong></em> (DAC) : Permet aux propriétaires d&#39;accorder des autorisations à des ressources.</li><li><em><strong>Contrôle d&#39;accès basé sur les rôles</strong></em> (RBAC) : Accorde l&#39;accès en fonction des rôles des utilisateurs.</li><li><em><strong>Contrôle d&#39;accès basé sur les attributs</strong></em> (ABAC) : Considère les attributs des identités des utilisateurs pour l&#39;autorisation.</li></ol><h3 id="firewalls-et-controle-d-acces-base-sur-les-regles" tabindex="-1">Firewalls et contrôle d&#39;accès basé sur les règles : <a class="header-anchor" href="#firewalls-et-controle-d-acces-base-sur-les-regles" aria-label="Permalink to &quot;Firewalls et contrôle d&#39;accès basé sur les règles :&quot;">​</a></h3><p>Les pare-feu appliquent les politiques de sécurité en utilisant des règles prédéfinies.</p><h3 id="gestion-des-acces-privilegies-pam" tabindex="-1">Gestion des accès privilégiés (PAM) : <a class="header-anchor" href="#gestion-des-acces-privilegies-pam" aria-label="Permalink to &quot;Gestion des accès privilégiés (PAM) :&quot;">​</a></h3><p>Gère et surveille les comptes privilégiés pour renforcer la sécurité.</p><p>Comprendre ces concepts et technologies est essentiel pour la préparation à l&#39;examen Security+ et pour la mise en œuvre de solutions d&#39;authentification et d&#39;autorisation robustes dans les organisations.</p><h2 id="implementation-d-une-infrastructure-a-cle-publique-pki-objectif-3-9" tabindex="-1">Implémentation d&#39;une infrastructure à clé publique (PKI) (Objectif 3.9) <a class="header-anchor" href="#implementation-d-une-infrastructure-a-cle-publique-pki-objectif-3-9" aria-label="Permalink to &quot;Implémentation d&#39;une infrastructure à clé publique (PKI) (Objectif 3.9)&quot;">​</a></h2><p>La PKI repose sur la cryptographie asymétrique et permet aux utilisateurs de partager leurs clés publiques de manière sécurisée avec d&#39;autres et de fournir à ces derniers l&#39;assurance que ces clés sont légitimes.</p><h3 id="mecanismes-de-partage-de-cles" tabindex="-1">Mécanismes de partage de clés <a class="header-anchor" href="#mecanismes-de-partage-de-cles" aria-label="Permalink to &quot;Mécanismes de partage de clés&quot;">​</a></h3><ol><li><em><strong>Certificats numériques</strong></em>: Des fichiers contenant la clé publique d&#39;un utilisateur et signés numériquement par une autorité de certification (CA).</li><li><em><strong>Autorité de certification</strong></em> (CA): Une tierce partie de confiance qui signe les certificats numériques.</li></ol><h3 id="processus-de-validation-des-certificats" tabindex="-1">Processus de validation des certificats <a class="header-anchor" href="#processus-de-validation-des-certificats" aria-label="Permalink to &quot;Processus de validation des certificats&quot;">​</a></h3><ol><li>Validation de la signature numérique: Confirmer l&#39;authenticité de la signature numérique par une CA de confiance.</li><li>Vérification de la validité du certificat: Vérifier si le certificat n&#39;a pas expiré.</li><li>Révocation des certificats: Les CA peuvent révoquer des certificats compromis.</li></ol><h3 id="methodes-de-revocation" tabindex="-1">Méthodes de révocation <a class="header-anchor" href="#methodes-de-revocation" aria-label="Permalink to &quot;Méthodes de révocation&quot;">​</a></h3><ol><li><em><strong>Liste de révocation de certificats</strong></em> (CRL): Liste des certificats invalides consultée lors de la validation.</li><li><em><strong>Protocole de statut de certificat en ligne</strong></em> (OCSP): Validation en temps réel du statut du certificat.</li></ol><h3 id="processus-de-creation-de-certificats" tabindex="-1">Processus de création de certificats <a class="header-anchor" href="#processus-de-creation-de-certificats" aria-label="Permalink to &quot;Processus de création de certificats&quot;">​</a></h3><ol><li><em><strong>Demande de signature de certificat</strong></em> (CSR): Requête envoyée à une CA pour obtenir un certificat.</li><li><em><strong>Émission de certificats</strong></em>: Les CA valident l&#39;identité de l&#39;utilisateur et émettent un certificat.</li></ol><h3 id="types-de-certificats" tabindex="-1">Types de certificats <a class="header-anchor" href="#types-de-certificats" aria-label="Permalink to &quot;Types de certificats&quot;">​</a></h3><ol><li>Certificats de validation de domaine (DV): Valident le contrôle du domaine.</li><li>Certificats de validation étendue (EV): Valident l&#39;identité de l&#39;organisation.</li></ol><h3 id="formats-de-certificats" tabindex="-1">Formats de certificats <a class="header-anchor" href="#formats-de-certificats" aria-label="Permalink to &quot;Formats de certificats&quot;">​</a></h3><ol><li>DER: Format binaire généralement avec les extensions .der, .crt ou .cer.</li><li>PEM: Format texte ASCII, avec les extensions .pem ou .crt.</li><li>PFX: Format binaire utilisé par les systèmes Windows, avec les extensions .pfx ou .p12.</li></ol><h3 id="utilisation-de-certificats-auto-signes" tabindex="-1">Utilisation de certificats auto-signés <a class="header-anchor" href="#utilisation-de-certificats-auto-signes" aria-label="Permalink to &quot;Utilisation de certificats auto-signés&quot;">​</a></h3><p>Les organisations peuvent créer leurs propres certificats, mais ils ne sont généralement pas fiables en dehors de l&#39;organisation.</p><p>Comprendre les concepts et processus liés à la PKI est essentiel pour réussir l&#39;examen Security+ et pour mettre en œuvre des solutions sécurisées dans les organisations.</p><hr><br><div class="info custom-block"><p class="custom-block-title">Sources</p><p>Ce cours s&#39;inspire du cours sur Linkedin eLearning de <em><strong>Mike CHAPPLE</strong></em> :</p><p><a href="https://www.linkedin.com/learning/comptia-security-plus-sy0-601-cert-prep-1-threats-attacks-and-vulnerabilities?contextUrn=urn%3Ali%3AlyndaLearningPath%3A57bdd64992015ae4c0cb990e&amp;dApp=36524473&amp;leis=LAA&amp;u=98048682" target="_blank" rel="noreferrer">https://www.linkedin.com/learning/comptia-security-plus-sy0-601-cert-prep-1-threats-attacks-and-vulnerabilities?contextUrn=urn%3Ali%3AlyndaLearningPath%3A57bdd64992015ae4c0cb990e&amp;dApp=36524473&amp;leis=LAA&amp;u=98048682</a></p></div>',141);function p(m,h,f,g,b,v){const s=t("Badge");return l(),a("div",null,[c,r(s,{type:"tip",text:"Rédigé le 24/03/2024"}),d])}const x=i(u,[["render",p]]);export{P as __pageData,x as default};
