import{_ as n,c as t,j as i,a as o,G as r,an as a,B as l,o as d}from"./chunks/framework.ys1W0Hiz.js";const b=JSON.parse('{"title":"Gouvernance, risques et conformité","description":"","frontmatter":{"sidebar":"auto"},"headers":[],"relativePath":"certification/securityplus/concepts/grc.md","filePath":"certification/securityplus/concepts/grc.md","lastUpdated":1729429463000}'),u={name:"certification/securityplus/concepts/grc.md"};function c(p,e,m,g,f,q){const s=l("Badge");return d(),t("div",null,[e[0]||(e[0]=i("h1",{id:"gouvernance-risques-et-conformite",tabindex:"-1"},[o("Gouvernance, risques et conformité "),i("a",{class:"header-anchor",href:"#gouvernance-risques-et-conformite","aria-label":'Permalink to "Gouvernance, risques et conformité"'},"​")],-1)),r(s,{type:"tip",text:"Rédigé le 26/03/2024"}),e[1]||(e[1]=a('<h2 id="comparer-et-contraster-differents-types-de-controles-de-securite-objectif-5-1" tabindex="-1">Comparer et contraster différents types de contrôles de sécurité (Objectif 5.1) <a class="header-anchor" href="#comparer-et-contraster-differents-types-de-controles-de-securite-objectif-5-1" aria-label="Permalink to &quot;Comparer et contraster différents types de contrôles de sécurité (Objectif 5.1)&quot;">​</a></h2><h3 id="regroupement-par-objectif-ou-type-de-controle" tabindex="-1">Regroupement par objectif ou type de contrôle <a class="header-anchor" href="#regroupement-par-objectif-ou-type-de-controle" aria-label="Permalink to &quot;Regroupement par objectif ou type de contrôle&quot;">​</a></h3><h4 id="controles-preventifs" tabindex="-1">Contrôles préventifs: <a class="header-anchor" href="#controles-preventifs" aria-label="Permalink to &quot;Contrôles préventifs:&quot;">​</a></h4><p>Empêchent les problèmes de sécurité dès le départ.</p><p>Exemple: Pare-feu bloquant le trafic réseau non désiré.</p><h4 id="controles-detecteurs" tabindex="-1">Contrôles détecteurs: <a class="header-anchor" href="#controles-detecteurs" aria-label="Permalink to &quot;Contrôles détecteurs:&quot;">​</a></h4><p>Identifient les violations potentielles nécessitant une enquête supplémentaire.</p><p>Exemple: Système de détection d&#39;intrusion recherchant des signes de violations réseau.</p><h4 id="controles-correctifs" tabindex="-1">Contrôles correctifs: <a class="header-anchor" href="#controles-correctifs" aria-label="Permalink to &quot;Contrôles correctifs:&quot;">​</a></h4><p>Corrigent les problèmes de sécurité déjà survenus.</p><p>Exemple: Restauration d&#39;informations critiques à partir de sauvegardes après une attaque.</p><h4 id="controles-dissuasifs" tabindex="-1">Contrôles dissuasifs: <a class="header-anchor" href="#controles-dissuasifs" aria-label="Permalink to &quot;Contrôles dissuasifs:&quot;">​</a></h4><p>Empêchent les attaquants de violer les politiques de sécurité.</p><p>Exemple: Chiens de garde vicieux et clôtures barbelées.</p><h4 id="controles-compensatoires" tabindex="-1">Contrôles compensatoires: <a class="header-anchor" href="#controles-compensatoires" aria-label="Permalink to &quot;Contrôles compensatoires:&quot;">​</a></h4><p>Comblent une lacune connue dans l&#39;environnement de sécurité.</p><p>Exemple: Placer un garde à une porte pour surveiller les entrées, en complément d&#39;une clôture.</p><h3 id="regroupement-par-mecanisme-d-action" tabindex="-1">Regroupement par mécanisme d&#39;action <a class="header-anchor" href="#regroupement-par-mecanisme-d-action" aria-label="Permalink to &quot;Regroupement par mécanisme d&#39;action&quot;">​</a></h3><h4 id="controles-techniques" tabindex="-1">Contrôles techniques: <a class="header-anchor" href="#controles-techniques" aria-label="Permalink to &quot;Contrôles techniques:&quot;">​</a></h4><p>Utilisent la technologie pour atteindre des objectifs de sécurité.</p><p>Exemple: Pare-feu, système de prévention d&#39;intrusion, chiffrement.</p><h4 id="controles-operationnels" tabindex="-1">Contrôles opérationnels: <a class="header-anchor" href="#controles-operationnels" aria-label="Permalink to &quot;Contrôles opérationnels:&quot;">​</a></h4><p>Processus mis en place pour gérer la technologie de manière sécurisée.</p><p>Exemple: Révisions des accès utilisateurs, surveillance des journaux, formation à la sécurité.</p><h4 id="controles-manageriaux" tabindex="-1">Contrôles managériaux: <a class="header-anchor" href="#controles-manageriaux" aria-label="Permalink to &quot;Contrôles managériaux:&quot;">​</a></h4><p>Se concentrent sur les mécanismes du processus de gestion des risques.</p><p>Exemple: Évaluations régulières des risques, intégration de la sécurité dans les méthodologies de gestion des changements.</p><p>Comprendre ces différents types de contrôles de sécurité est essentiel pour réussir l&#39;examen Security Plus.</p><h2 id="importance-des-reglementations-normes-ou-cadres-applicables-a-la-posture-de-securite-d-une-organisation-objectif-5-2" tabindex="-1">Importance des réglementations, normes ou cadres applicables à la posture de sécurité d&#39;une organisation (Objectif 5.2) <a class="header-anchor" href="#importance-des-reglementations-normes-ou-cadres-applicables-a-la-posture-de-securite-d-une-organisation-objectif-5-2" aria-label="Permalink to &quot;Importance des réglementations, normes ou cadres applicables à la posture de sécurité d&#39;une organisation (Objectif 5.2)&quot;">​</a></h2><h3 id="reglementations-et-legislations" tabindex="-1">Réglementations et législations <a class="header-anchor" href="#reglementations-et-legislations" aria-label="Permalink to &quot;Réglementations et législations&quot;">​</a></h3><ol><li><p>Réglementation de l&#39;Union Européenne:</p><ul><li><em><strong>GDPR (General Data Protection Regulation)</strong></em>: Régule la manipulation des informations personnelles des résidents de l&#39;Union Européenne.</li></ul></li><li><p>Normes de sécurité des données de l&#39;industrie des cartes de paiement (PCI DSS):</p><ul><li>Règlementation privée régissant les informations de cartes de crédit.</li></ul></li></ol><h3 id="cadres-de-securite" tabindex="-1">Cadres de sécurité <a class="header-anchor" href="#cadres-de-securite" aria-label="Permalink to &quot;Cadres de sécurité&quot;">​</a></h3><ol><li><p>Center for Internet Security (CIS):</p><ul><li><em><strong>Benchmarks et Standards de Configuration</strong></em>: Fournissent des recommandations de configuration pour sécuriser les systèmes.</li></ul></li><li><p>National Institute of Standards and Technology (NIST):</p><ul><li><em><strong>Risk Management Framework</strong></em> (RMF): Cadre pour la gestion des risques.</li><li><em><strong>Cybersecurity Framework</strong></em> (CSF): Cadre pour améliorer la cybersécurité d&#39;une organisation.</li></ul></li><li><p>Organisation Internationale de Normalisation (ISO):</p><ul><li><em><strong>ISO 27001</strong></em>: Gestion de la sécurité de l&#39;information.</li><li><em><strong>ISO 27002</strong></em>: Ensemble de contrôles de sécurité de l&#39;information.</li><li><em><strong>ISO 27701</strong></em>: Normes de confidentialité de l&#39;information.</li><li><em><strong>ISO 31000</strong></em>: Gestion des risques.</li></ul></li><li><p>Cloud Security Alliance:</p><ul><li><em><strong>Matrice de contrôle Cloud</strong></em>: Guide les professionnels de la sécurité travaillant dans le cloud.</li></ul></li></ol><h3 id="verification-des-normes-de-securite-des-fournisseurs-de-services-cloud" tabindex="-1">Vérification des normes de sécurité des fournisseurs de services Cloud <a class="header-anchor" href="#verification-des-normes-de-securite-des-fournisseurs-de-services-cloud" aria-label="Permalink to &quot;Vérification des normes de sécurité des fournisseurs de services Cloud&quot;">​</a></h3><ol><li>Audits SOC (Service Organization Control): <ul><li>SOC 2: Audit détaillé des contrôles de confidentialité, d&#39;intégrité, de disponibilité et de confidentialité d&#39;un fournisseur de services.</li><li>Rapports type 1: Description des contrôles en place avec l&#39;opinion de l&#39;auditeur sur leur adéquation.</li><li>Rapports type 2: Même opinion que le type 1, avec des tests pour vérifier le bon fonctionnement des contrôles.</li></ul></li></ol><p>En tant que candidat à l&#39;examen Security+, il est crucial de comprendre ces réglementations, normes et cadres pour garantir une posture de sécurité efficace pour une organisation.</p><h2 id="importance-des-politiques-pour-la-securite-organisationnelle-objectif-5-3" tabindex="-1">Importance des politiques pour la sécurité organisationnelle (Objectif 5.3) <a class="header-anchor" href="#importance-des-politiques-pour-la-securite-organisationnelle-objectif-5-3" aria-label="Permalink to &quot;Importance des politiques pour la sécurité organisationnelle (Objectif 5.3)&quot;">​</a></h2><h3 id="politiques-de-securite-du-personnel" tabindex="-1">Politiques de sécurité du personnel <a class="header-anchor" href="#politiques-de-securite-du-personnel" aria-label="Permalink to &quot;Politiques de sécurité du personnel&quot;">​</a></h3><ol><li><p>Politique d&#39;utilisation acceptable:</p><ul><li>Définit les façons dont les employés sont autorisés à utiliser les actifs technologiques de l&#39;organisation.</li></ul></li><li><p>Politique de rotation des emplois:</p><ul><li>Déplace le personnel dans des postes sensibles à travers une série de différents emplois pour éviter qu&#39;ils restent dans une même position sensible pendant une longue période.</li></ul></li><li><p>Politique de congés obligatoires:</p><ul><li>Exige que les individus prennent du temps loin du bureau pour permettre de découvrir toute activité frauduleuse éventuelle.</li></ul></li><li><p>Politique de séparation des fonctions:</p><ul><li>Interdit à une personne d&#39;avoir deux autorisations différentes qui, combinées, leur permettent d&#39;effectuer des actions sensibles.</li></ul></li><li><p>Politique du privilège minimum:</p><ul><li>Les individus doivent avoir le jeu de permissions minimum nécessaire pour effectuer leurs fonctions.</li></ul></li><li><p>Politique de bureau propre:</p><ul><li>Assure que les documents sensibles ne sont pas laissés à découvert où quelqu&#39;un pourrait les observer.</li></ul></li><li><p>Vérification des antécédents:</p><ul><li>Vérifie l&#39;historique criminel avant d&#39;embaucher de nouveaux employés.</li></ul></li><li><p>Accords de non-divulgation (NDA):</p><ul><li>Les employés acceptent de maintenir la confidentialité des informations sensibles.</li></ul></li></ol><h3 id="politiques-de-gestion-des-risques-tiers" tabindex="-1">Politiques de gestion des risques tiers <a class="header-anchor" href="#politiques-de-gestion-des-risques-tiers" aria-label="Permalink to &quot;Politiques de gestion des risques tiers&quot;">​</a></h3><ol><li><p>Accord de niveau de service (SLA):</p><ul><li>Définit les attentes de performance envers le fournisseur et les conséquences en cas de non-respect des normes.</li></ul></li><li><p>Accords de partenariat commercial (BPA):</p><ul><li>Établit les paramètres d&#39;un partenariat avec une organisation externe.</li></ul></li></ol><h3 id="politiques-de-gestion-des-donnees" tabindex="-1">Politiques de gestion des données <a class="header-anchor" href="#politiques-de-gestion-des-donnees" aria-label="Permalink to &quot;Politiques de gestion des données&quot;">​</a></h3><ol><li><p>Politique de classification des données:</p><ul><li>Exigences pour classer les données en fonction de leur sensibilité.</li></ul></li><li><p>Politique de gouvernance des données:</p><ul><li>Procédures pour gérer le cycle de vie des données.</li></ul></li><li><p>Politique de conservation des données:</p><ul><li>Détermine quelles données l&#39;organisation conservera et pendant combien de temps.</li></ul></li></ol><h3 id="politiques-de-gestion-des-identifiants" tabindex="-1">Politiques de gestion des identifiants <a class="header-anchor" href="#politiques-de-gestion-des-identifiants" aria-label="Permalink to &quot;Politiques de gestion des identifiants&quot;">​</a></h3><ol><li>Politique des identifiants: <ul><li>Exigences pour la gestion des mots de passe et autres identifiants.</li></ul></li></ol><h3 id="politiques-de-gestion-des-changements-et-des-actifs" tabindex="-1">Politiques de gestion des changements et des actifs <a class="header-anchor" href="#politiques-de-gestion-des-changements-et-des-actifs" aria-label="Permalink to &quot;Politiques de gestion des changements et des actifs&quot;">​</a></h3><ol><li>Politique de gestion des changements: <ul><li>Procédures pour s&#39;assurer que les systèmes sont maintenus correctement.</li></ul></li></ol><p>En tant que candidat à l&#39;examen Security+, comprendre et appliquer ces politiques est essentiel pour assurer la sécurité organisationnelle.</p><h2 id="resume-des-processus-et-concepts-de-gestion-des-risques-objectif-5-4" tabindex="-1">Résumé des processus et concepts de gestion des risques (Objectif 5.4) <a class="header-anchor" href="#resume-des-processus-et-concepts-de-gestion-des-risques-objectif-5-4" aria-label="Permalink to &quot;Résumé des processus et concepts de gestion des risques (Objectif 5.4)&quot;">​</a></h2><h3 id="types-de-risques" tabindex="-1">Types de risques <a class="header-anchor" href="#types-de-risques" aria-label="Permalink to &quot;Types de risques&quot;">​</a></h3><ol><li><p>Risques externes:</p><ul><li>Exemples: Hackers, menaces extérieures à l&#39;organisation.</li></ul></li><li><p>Risques internes:</p><ul><li>Exemples: Employés malveillants, problèmes de conformité en matière de licences logicielles.</li></ul></li></ol><h3 id="strategies-de-gestion-des-risques" tabindex="-1">Stratégies de gestion des risques <a class="header-anchor" href="#strategies-de-gestion-des-risques" aria-label="Permalink to &quot;Stratégies de gestion des risques&quot;">​</a></h3><ol><li><p>Mitigation des risques:</p><ul><li>Réduit la probabilité ou l&#39;impact d&#39;un risque potentiel.</li></ul></li><li><p>Transfert des risques:</p><ul><li>Déplace une partie du risque vers une organisation externe, souvent par l&#39;achat d&#39;une assurance.</li></ul></li><li><p>Évitement des risques:</p><ul><li>Modification des pratiques commerciales pour rendre un risque irrélevant.</li></ul></li><li><p>Acceptation des risques:</p><ul><li>Reconnaitre qu&#39;un risque existe mais décider de poursuivre les activités malgré ce risque.</li></ul></li></ol><h3 id="processus-de-gestion-des-risques" tabindex="-1">Processus de gestion des risques <a class="header-anchor" href="#processus-de-gestion-des-risques" aria-label="Permalink to &quot;Processus de gestion des risques&quot;">​</a></h3><ol><li>Enregistrement des risques dans un registre des risques.</li><li>Évaluation des risques par une matrice ou une carte de chaleur.</li><li>Détermination de la tolérance aux risques.</li><li>Identification du risque inhérent et du risque résiduel.</li><li>Analyse des risques qualitative ou quantitative.</li></ol><h3 id="analyse-des-risques-quantitative" tabindex="-1">Analyse des risques quantitative <a class="header-anchor" href="#analyse-des-risques-quantitative" aria-label="Permalink to &quot;Analyse des risques quantitative&quot;">​</a></h3><ol><li><em><strong>Perte espérée unique</strong></em> (SLE): Mesure des dommages financiers en cas de matérialisation du risque.</li><li><em><strong>Taux de fréquence annuel</strong></em> (ARO): Nombre de fois où l&#39;on s&#39;attend à ce que le risque se matérialise chaque année.</li><li><em><strong>Perte espérée annuelle</strong></em> (ALE): Mesure globale du risque.</li></ol><h3 id="plan-de-reprise-d-activite-pra" tabindex="-1">Plan de reprise d&#39;activité (PRA) <a class="header-anchor" href="#plan-de-reprise-d-activite-pra" aria-label="Permalink to &quot;Plan de reprise d&#39;activité (PRA)&quot;">​</a></h3><ol><li><em><strong>Objectif de temps de récupération</strong></em> (RTO): Durée maximale d&#39;interruption tolérable.</li><li><em><strong>Objectif de point de récupération</strong></em> (RPO): Perte de données acceptable en cas de perturbation.</li><li><em><strong>Temps moyen entre les pannes</strong></em> (MTBF): Fréquence prévue des pannes d&#39;équipement.</li><li><em><strong>Temps moyen de réparation</strong></em> (MTTR): Durée typique pour réparer l&#39;équipement.</li></ol><h3 id="plan-de-reprise-apres-sinistre-drp" tabindex="-1">Plan de reprise après sinistre (DRP) <a class="header-anchor" href="#plan-de-reprise-apres-sinistre-drp" aria-label="Permalink to &quot;Plan de reprise après sinistre (DRP)&quot;">​</a></h3><ol><li>Identifie les fonctions essentielles à la mission et les systèmes critiques.</li><li>Effectue une évaluation des risques de ces fonctions.</li><li>Identifie les plans de récupération pour restaurer le service après un sinistre.</li></ol><p>En résumé, la gestion des risques implique l&#39;identification, l&#39;évaluation et la gestion des risques pour assurer la continuité des opérations et la résilience de l&#39;organisation face aux menaces potentielles.</p><h2 id="concepts-de-confidentialite-et-de-donnees-sensibles-en-relation-avec-la-securite-objectif-5-5" tabindex="-1">Concepts de confidentialité et de données sensibles en relation avec la sécurité (Objectif 5.5) <a class="header-anchor" href="#concepts-de-confidentialite-et-de-donnees-sensibles-en-relation-avec-la-securite-objectif-5-5" aria-label="Permalink to &quot;Concepts de confidentialité et de données sensibles en relation avec la sécurité (Objectif 5.5)&quot;">​</a></h2><h3 id="consequences-organisationnelles-des-violations-de-la-confidentialite-et-des-donnees" tabindex="-1">Conséquences organisationnelles des violations de la confidentialité et des données <a class="header-anchor" href="#consequences-organisationnelles-des-violations-de-la-confidentialite-et-des-donnees" aria-label="Permalink to &quot;Conséquences organisationnelles des violations de la confidentialité et des données&quot;">​</a></h3><ol><li><em><strong>Dommages financiers</strong></em>: amendes et pénalités.</li><li><em><strong>Dommages de réputation</strong></em>: impact sur la perception publique.</li><li><em><strong>Vol d&#39;identité</strong></em>: conséquences pour les clients, employés et autres parties prenantes.</li><li><em><strong>Perte de propriété intellectuelle</strong></em>: risques associés à la divulgation non autorisée d&#39;informations sensibles.</li></ol><h3 id="classification-des-donnees" tabindex="-1">Classification des données <a class="header-anchor" href="#classification-des-donnees" aria-label="Permalink to &quot;Classification des données&quot;">​</a></h3><ol><li><em><strong>Niveaux de classification</strong></em>: public, privé, sensible, confidentiel, critique, propriétaire, etc.</li><li><em><strong>Catégories de données sensibles</strong></em>: PII (Informations Personnellement Identifiables), informations de santé, informations financières, données client, etc.</li></ol><h3 id="technologies-ameliorant-la-confidentialite" tabindex="-1">Technologies améliorant la confidentialité <a class="header-anchor" href="#technologies-ameliorant-la-confidentialite" aria-label="Permalink to &quot;Technologies améliorant la confidentialité&quot;">​</a></h3><ol><li><em><strong>Minimisation des données</strong></em>: ne conserver que les données essentielles à l&#39;activité.</li><li><em><strong>Masquage des données</strong></em>: suppression des éléments sensibles.</li><li><em><strong>Tokenization</strong></em>: remplacement des éléments sensibles par des valeurs alternatives.</li><li><em><strong>Anonymisation</strong></em> et <em><strong>pseudo-anonymisation</strong></em>: suppression des éléments d&#39;identification personnelle.</li></ol><h3 id="roles-et-responsabilites-dans-la-gestion-des-donnees" tabindex="-1">Rôles et responsabilités dans la gestion des données <a class="header-anchor" href="#roles-et-responsabilites-dans-la-gestion-des-donnees" aria-label="Permalink to &quot;Rôles et responsabilités dans la gestion des données&quot;">​</a></h3><ol><li><em><strong>Propriétaire des données</strong></em>: responsable global des données.</li><li><em><strong>Gestionnaire et intendant des données</strong></em>: responsables des opérations quotidiennes de gestion des données.</li><li><em><strong>Contrôleur des données</strong></em>: organisation déterminant comment et pourquoi les données personnelles sont traitées.</li><li><em><strong>Processeur de données</strong></em>: organisation tierce traitant des données pour le compte du contrôleur des données.</li><li><em><strong>Officier de protection des données</strong></em> (DPO): responsable de la mise en œuvre des politiques de confidentialité et de la liaison principale pour les questions de confidentialité.</li></ol><h3 id="controles-de-securite-tout-au-long-du-cycle-de-vie-des-informations" tabindex="-1">Contrôles de sécurité tout au long du cycle de vie des informations <a class="header-anchor" href="#controles-de-securite-tout-au-long-du-cycle-de-vie-des-informations" aria-label="Permalink to &quot;Contrôles de sécurité tout au long du cycle de vie des informations&quot;">​</a></h3><ol><li>Définir des contrôles de sécurité pour suivre les informations du début à la fin de leur cycle de vie.</li><li>Effectuer des évaluations d&#39;impact pour identifier les points d&#39;exposition potentiels des informations.</li></ol><h3 id="politique-de-confidentialite" tabindex="-1">Politique de confidentialité <a class="header-anchor" href="#politique-de-confidentialite" aria-label="Permalink to &quot;Politique de confidentialité&quot;">​</a></h3><ol><li>Afficher une politique de confidentialité claire qui informe les parties prenantes sur la manière dont l&#39;organisation traite leurs informations conformément aux accords en vigueur.</li></ol><p>En conclusion, la gestion efficace de la confidentialité et des données sensibles est essentielle pour prévenir les violations de données et protéger la réputation et la crédibilité de l&#39;organisation.</p><hr><br><div class="info custom-block"><p class="custom-block-title">Sources</p><p>Ce cours s&#39;inspire du cours sur Linkedin eLearning de <em><strong>Mike CHAPPLE</strong></em> :</p><p><a href="https://www.linkedin.com/learning/comptia-security-plus-sy0-601-cert-prep-1-threats-attacks-and-vulnerabilities?contextUrn=urn%3Ali%3AlyndaLearningPath%3A57bdd64992015ae4c0cb990e&amp;dApp=36524473&amp;leis=LAA&amp;u=98048682" target="_blank" rel="noreferrer">https://www.linkedin.com/learning/comptia-security-plus-sy0-601-cert-prep-1-threats-attacks-and-vulnerabilities?contextUrn=urn%3Ali%3AlyndaLearningPath%3A57bdd64992015ae4c0cb990e&amp;dApp=36524473&amp;leis=LAA&amp;u=98048682</a></p></div>',79))])}const v=n(u,[["render",c]]);export{b as __pageData,v as default};
