import{_ as i,c as n,j as s,a as r,G as a,an as o,B as l,o as u}from"./chunks/framework.ys1W0Hiz.js";const v=JSON.parse('{"title":"Opérations et réponses aux incidents","description":"","frontmatter":{"sidebar":"auto"},"headers":[],"relativePath":"certification/securityplus/concepts/oir.md","filePath":"certification/securityplus/concepts/oir.md","lastUpdated":1729429463000}'),d={name:"certification/securityplus/concepts/oir.md"};function c(p,e,m,h,g,f){const t=l("Badge");return u(),n("div",null,[e[0]||(e[0]=s("h1",{id:"operations-et-reponses-aux-incidents",tabindex:"-1"},[r("Opérations et réponses aux incidents "),s("a",{class:"header-anchor",href:"#operations-et-reponses-aux-incidents","aria-label":'Permalink to "Opérations et réponses aux incidents"'},"​")],-1)),a(t,{type:"tip",text:"Rédigé le 25/03/2024"}),e[1]||(e[1]=o('<h2 id="utilisation-d-outils-appropries-pour-evaluer-la-securite-organisationnelle-objectif-4-1" tabindex="-1">Utilisation d&#39;outils appropriés pour évaluer la sécurité organisationnelle (Objectif 4.1) <a class="header-anchor" href="#utilisation-d-outils-appropries-pour-evaluer-la-securite-organisationnelle-objectif-4-1" aria-label="Permalink to &quot;Utilisation d&#39;outils appropriés pour évaluer la sécurité organisationnelle (Objectif 4.1)&quot;">​</a></h2><p>Dans cet objectif, il est essentiel d&#39;être familiarisé avec un large éventail d&#39;outils de sécurité.</p><h3 id="reconnaissance-et-decouverte-du-reseau" tabindex="-1">Reconnaissance et découverte du réseau <a class="header-anchor" href="#reconnaissance-et-decouverte-du-reseau" aria-label="Permalink to &quot;Reconnaissance et découverte du réseau&quot;">​</a></h3><ol><li><em><strong>Traceroute</strong></em>: Identifie le chemin réseau entre deux systèmes.</li><li><em><strong>Ping</strong></em>: Teste si un système distant est connecté et opérationnel sur le réseau.</li><li><em><strong>Hping</strong></em>: Version personnalisable de Ping permettant de modifier les paquets utilisés dans l&#39;analyse.</li><li><em><strong>PathPing</strong></em>: Outil Windows combinant les fonctionnalités de Ping et Traceroute.</li><li><em><strong>Netstat</strong></em>: Affiche les connexions réseau actives sur un périphérique.</li><li><em><strong>nc</strong></em> (netcat): Permet d&#39;envoyer et de recevoir du texte brut via une connexion réseau.</li><li><em><strong>ipconfig</strong></em> (Windows) / <em><strong>ifconfig</strong></em> (Mac et Linux): Affiche et modifie la configuration d&#39;une interface réseau.</li><li><em><strong>nslookup</strong></em> et <em><strong>dig</strong></em>: Effectuent des requêtes DNS.</li><li><em><strong>ARP</strong></em>: Effectue des recherches à l&#39;aide du protocole de résolution d&#39;adresses.</li></ol><h3 id="manipulation-de-fichiers" tabindex="-1">Manipulation de fichiers <a class="header-anchor" href="#manipulation-de-fichiers" aria-label="Permalink to &quot;Manipulation de fichiers&quot;">​</a></h3><p>Ces commandes sont couramment utilisées sur les systèmes Linux :</p><ol><li><em><strong>head</strong></em>: Affiche les premières lignes d&#39;un fichier.</li><li><em><strong>tail</strong></em>: Affiche les dernières lignes d&#39;un fichier.</li><li><em><strong>cat</strong></em>: Affiche tout le contenu d&#39;un fichier.</li><li><em><strong>grep</strong></em>: Recherche du contenu dans un fichier.</li><li><em><strong>chmod</strong></em>: Modifie les permissions d&#39;un fichier.</li><li><em><strong>logger</strong></em>: Envoie des entrées de journal à un serveur de journaux centralisé.</li></ol><h3 id="environnements-de-shell-et-de-script" tabindex="-1">Environnements de shell et de script <a class="header-anchor" href="#environnements-de-shell-et-de-script" aria-label="Permalink to &quot;Environnements de shell et de script&quot;">​</a></h3><ol><li><em><strong>SSH</strong></em>: Accès sécurisé aux systèmes distants (Linux).</li><li><em><strong>PowerShell</strong></em>: Environnement de script pour le contrôle administratif des systèmes Windows.</li><li><em><strong>Python</strong></em>: Langage de programmation généraliste utilisé pour l&#39;administration système.</li><li><em><strong>OpenSSL</strong></em>: Implémentation open-source du protocole TLS.</li></ol><h3 id="outils-de-capture-et-de-rejeu-de-paquets" tabindex="-1">Outils de capture et de rejeu de paquets <a class="header-anchor" href="#outils-de-capture-et-de-rejeu-de-paquets" aria-label="Permalink to &quot;Outils de capture et de rejeu de paquets&quot;">​</a></h3><ol><li><em><strong>tcpdump</strong></em>: Utilitaire en ligne de commande pour capturer et enregistrer le trafic réseau.</li><li><em><strong>Wireshark</strong></em>: Outil graphique offrant des fonctionnalités similaires à tcpdump.</li><li><em><strong>tcpreplay</strong></em>: Rejoue le trafic réseau capturé avec tcpdump ou Wireshark.</li></ol><h3 id="outils-forensiques" tabindex="-1">Outils forensiques <a class="header-anchor" href="#outils-forensiques" aria-label="Permalink to &quot;Outils forensiques&quot;">​</a></h3><ol><li><em><strong>dd</strong></em>: Commande de sauvegarde de disque pour créer des images forensiques de disques durs.</li><li><em><strong>mem dump</strong></em>: Sauvegarde le contenu actuel de la mémoire de l&#39;ordinateur.</li><li><em><strong>WinHex</strong></em>: Éditeur hexadécimal utile en forensique.</li><li><em><strong>FTK</strong></em> et <em><strong>Autopsy</strong></em>: Suites fournissant des capacités forensiques avancées.</li></ol><h4 id="en-plus-de-ces-categories-d-outils-il-est-egalement-important-de-connaitre-l-utilisation-de" tabindex="-1">En plus de ces catégories d&#39;outils, il est également important de connaître l&#39;utilisation de : <a class="header-anchor" href="#en-plus-de-ces-categories-d-outils-il-est-egalement-important-de-connaitre-l-utilisation-de" aria-label="Permalink to &quot;En plus de ces catégories d&#39;outils, il est également important de connaître l&#39;utilisation de :&quot;">​</a></h4><ol><li><em><strong>Cadres d&#39;exploitation</strong></em>: Pour automatiser les tests de pénétration et autres activités de sécurité.</li><li><em><strong>Crackeurs de mots de passe</strong></em>: Pour tenter des attaques de force brute contre les fichiers de mots de passe.</li><li><em><strong>Outils de désinfection de données</strong></em>: Pour purger définitivement les informations des supports.</li></ol><p>Ces outils sont essentiels pour préparer l&#39;examen Security+ et pour mener à bien les opérations de sécurité dans une organisation.</p><h2 id="importance-des-politiques-processus-et-procedures-objectif-4-2" tabindex="-1">Importance des politiques, processus et procédures (Objectif 4.2) <a class="header-anchor" href="#importance-des-politiques-processus-et-procedures-objectif-4-2" aria-label="Permalink to &quot;Importance des politiques, processus et procédures (Objectif 4.2)&quot;">​</a></h2><p>La mise en place d&#39;un plan de réponse aux incidents est essentielle pour toute organisation afin de détecter et de répondre efficacement aux incidents de sécurité. Le processus de réponse aux incidents standard comprend six étapes principales :</p><h4 id="_1-preparation" tabindex="-1">1. Préparation <a class="header-anchor" href="#_1-preparation" aria-label="Permalink to &quot;1. Préparation&quot;">​</a></h4><p>Avant qu&#39;un incident ne se produise, il est crucial de mettre en place les bonnes politiques et procédures, de disposer des ressources nécessaires et de former les personnes pour réagir efficacement en cas d&#39;incident de sécurité.</p><h4 id="_2-identification" tabindex="-1">2. Identification <a class="header-anchor" href="#_2-identification" aria-label="Permalink to &quot;2. Identification&quot;">​</a></h4><p>Lorsqu&#39;un incident de sécurité est détecté par le centre opérationnel de sécurité de l&#39;organisation ou d&#39;autres ressources, l&#39;étape d&#39;identification commence.</p><h4 id="_3-containment-contenir" tabindex="-1">3. Containment (Contenir) <a class="header-anchor" href="#_3-containment-contenir" aria-label="Permalink to &quot;3. Containment (Contenir)&quot;">​</a></h4><p>La priorité est d&#39;isoler les dommages causés par l&#39;incident de sécurité pour limiter sa propagation. Cela peut impliquer la déconnexion des systèmes du réseau ou d&#39;autres actions pour contenir les dommages.</p><h4 id="_4-eradication" tabindex="-1">4. Éradication <a class="header-anchor" href="#_4-eradication" aria-label="Permalink to &quot;4. Éradication&quot;">​</a></h4><p>Une fois l&#39;incident contenu, l&#39;étape d&#39;éradication vise à annuler les effets de l&#39;incident de sécurité et à supprimer les ressources compromises de nos réseaux.</p><h4 id="_5-recuperation" tabindex="-1">5. Récupération <a class="header-anchor" href="#_5-recuperation" aria-label="Permalink to &quot;5. Récupération&quot;">​</a></h4><p>L&#39;organisation restaure et reprend ses opérations normales après avoir éradiqué l&#39;incident.</p><h4 id="_6-lecons-apprises" tabindex="-1">6. Leçons apprises <a class="header-anchor" href="#_6-lecons-apprises" aria-label="Permalink to &quot;6. Leçons apprises&quot;">​</a></h4><p>Une session d&#39;apprentissage est organisée pour réunir tous les acteurs principaux impliqués dans la réponse à l&#39;incident. Ils examinent l&#39;incident de sécurité et identifient des leçons à tirer pour améliorer la capacité de l&#39;organisation à détecter et à répondre à de futurs incidents.</p><h3 id="roles-des-exercices" tabindex="-1">Rôles des exercices <a class="header-anchor" href="#roles-des-exercices" aria-label="Permalink to &quot;Rôles des exercices&quot;">​</a></h3><p>Les exercices sont essentiels pour préparer l&#39;équipe de réponse aux incidents aux futurs incidents :</p><ol><li><em><strong>Exercices de table</strong></em>: Réunissent les membres de l&#39;équipe pour discuter de leurs rôles dans le processus de réponse aux incidents et peut-être simuler un scénario fourni par un facilitateur.</li><li><em><strong>Exercices de marche structurée</strong></em>: Similaires aux exercices de table, mais impliquent une revue plus détaillée des procédures de réponse aux incidents.</li><li><em><strong>Exercices de simulation</strong></em>: Créent un incident de sécurité fictif auquel l&#39;équipe répond comme s&#39;il s&#39;agissait d&#39;un incident réel.</li></ol><h3 id="gestion-des-parties-prenantes" tabindex="-1">Gestion des parties prenantes <a class="header-anchor" href="#gestion-des-parties-prenantes" aria-label="Permalink to &quot;Gestion des parties prenantes&quot;">​</a></h3><p>Il est crucial de gérer efficacement les parties prenantes, telles que les employés, les experts en la matière, la direction, les forces de l&#39;ordre et les médias, pour assurer une communication appropriée pendant la réponse à l&#39;incident.</p><h3 id="alignement-avec-la-continuite-des-operations" tabindex="-1">Alignement avec la continuité des opérations <a class="header-anchor" href="#alignement-avec-la-continuite-des-operations" aria-label="Permalink to &quot;Alignement avec la continuité des opérations&quot;">​</a></h3><p>La réponse aux incidents est étroitement liée aux efforts de planification de la continuité des opérations de l&#39;organisation. L&#39;équipe de réponse aux incidents doit donc également être familière avec le plan de continuité des activités et le plan de reprise après sinistre.</p><h3 id="gestion-des-enregistrements" tabindex="-1">Gestion des enregistrements <a class="header-anchor" href="#gestion-des-enregistrements" aria-label="Permalink to &quot;Gestion des enregistrements&quot;">​</a></h3><p>Tous les enregistrements générés pendant la réponse à l&#39;incident doivent être conformes à la politique de conservation de l&#39;organisation, qui décrit pendant combien de temps ces enregistrements seront conservés.</p><p>En résumé, comprendre l&#39;importance des politiques, processus et procédures de réponse aux incidents est crucial pour réussir l&#39;examen Security+.</p><h2 id="utilisation-de-sources-de-donnees-appropriees-pour-soutenir-une-enquete-objectif-4-3" tabindex="-1">Utilisation de sources de données appropriées pour soutenir une enquête (Objectif 4.3) <a class="header-anchor" href="#utilisation-de-sources-de-donnees-appropriees-pour-soutenir-une-enquete-objectif-4-3" aria-label="Permalink to &quot;Utilisation de sources de données appropriées pour soutenir une enquête (Objectif 4.3)&quot;">​</a></h2><p>Les intervenants en cas d&#39;incident disposent d&#39;une grande quantité d&#39;informations, notamment les résultats des analyses de vulnérabilités. Ces résultats sont cruciaux pour aider les intervenants à comprendre comment un intrus aurait pu accéder aux systèmes et identifier d&#39;autres systèmes susceptibles d&#39;être vulnérables aux mêmes exploits.</p><h3 id="role-des-systemes-de-gestion-des-informations-et-des-evenements-de-securite-siem" tabindex="-1">Rôle des systèmes de gestion des informations et des événements de sécurité (SIEM) <a class="header-anchor" href="#role-des-systemes-de-gestion-des-informations-et-des-evenements-de-securite-siem" aria-label="Permalink to &quot;Rôle des systèmes de gestion des informations et des événements de sécurité (SIEM)&quot;">​</a></h3><p>Les SIEM jouent un rôle important dans la facilitation de la réponse aux incidents, car ils servent de points d&#39;agrégation centralisés pour tous les journaux de sécurité et autres informations de l&#39;organisation. Les équipes de réponse aux incidents peuvent utiliser ces systèmes SIEM pour effectuer des analyses de tendances, répondre aux alertes et corréler les événements se produisant sur de nombreux systèmes différents.</p><h3 id="sources-de-donnees-cles" tabindex="-1">Sources de données clés <a class="header-anchor" href="#sources-de-donnees-cles" aria-label="Permalink to &quot;Sources de données clés&quot;">​</a></h3><ol><li><em><strong>Journaux de sécurité</strong></em>: Fournis par divers dispositifs tels que les routeurs, les systèmes d&#39;exploitation, les applications, les serveurs Web, les serveurs DNS, etc.</li><li><em><strong>Surveillance du réseau</strong></em>: Utilisation de moniteurs de bande passante, de journaux NetFlow et d&#39;analyseurs de protocoles.</li><li><em><strong>Métadonnées</strong></em>: Informations de base attachées aux messages électroniques, échanges web, fichiers et communications mobiles.</li></ol><h3 id="technologies-standardisees" tabindex="-1">Technologies standardisées <a class="header-anchor" href="#technologies-standardisees" aria-label="Permalink to &quot;Technologies standardisées&quot;">​</a></h3><p>Les dispositifs de collecte de données rapportent aux SIEM en utilisant des technologies standardisées comme le protocole <strong>syslog</strong>, offrant ainsi une norme ouverte pour la communication des entrées de journal de sécurité.</p><h3 id="importance-de-l-analyse-des-donnees" tabindex="-1">Importance de l&#39;analyse des données <a class="header-anchor" href="#importance-de-l-analyse-des-donnees" aria-label="Permalink to &quot;Importance de l&#39;analyse des données&quot;">​</a></h3><p>L&#39;analyse et la corrélation des données provenant de différentes sources peuvent fournir des informations cruciales sur l&#39;évolution d&#39;un incident de sécurité. Les métadonnées, en particulier, peuvent révéler des indices importants sur l&#39;origine des fichiers et des communications.</p><p>Comprendre l&#39;utilisation des sources de données appropriées pour soutenir une enquête est essentiel pour réussir l&#39;examen Security+. Ces informations permettent aux intervenants en cas d&#39;incident de comprendre la portée et l&#39;impact d&#39;un incident de sécurité et de prendre les mesures nécessaires pour y répondre de manière appropriée.</p><h2 id="application-de-techniques-d-attenuation-ou-de-controles-pour-securiser-un-environnement-objectif-4-4" tabindex="-1">Application de techniques d&#39;atténuation ou de contrôles pour sécuriser un environnement (Objectif 4.4) <a class="header-anchor" href="#application-de-techniques-d-attenuation-ou-de-controles-pour-securiser-un-environnement-objectif-4-4" aria-label="Permalink to &quot;Application de techniques d&#39;atténuation ou de contrôles pour sécuriser un environnement (Objectif 4.4)&quot;">​</a></h2><p>L&#39;objectif principal de ce sujet est la récupération, la restauration des opérations d&#39;une organisation, non seulement à l&#39;état dans lequel elles étaient avant un incident, mais à un état encore plus sécurisé qui ne soit pas vulnérable au même type d&#39;incident.</p><h3 id="reconfigurer-les-solutions-de-securite-des-points-d-extremite" tabindex="-1">Reconfigurer les solutions de sécurité des points d&#39;extrémité <a class="header-anchor" href="#reconfigurer-les-solutions-de-securite-des-points-d-extremite" aria-label="Permalink to &quot;Reconfigurer les solutions de sécurité des points d&#39;extrémité&quot;">​</a></h3><p>L&#39;une des mesures les plus importantes à prendre en réponse à un incident de sécurité est la reconfiguration des solutions de sécurité des points d&#39;extrémité pour éviter qu&#39;un incident similaire ne se reproduise à l&#39;avenir. Cela peut impliquer la mise en place d&#39;une quarantaine où la sécurité des points d&#39;extrémité est testée avant qu&#39;un système ne soit autorisé à rejoindre le réseau. Les dispositifs qui ne répondent pas aux normes de sécurité sont placés sur le réseau de quarantaine, où ils peuvent accéder aux ressources nécessaires pour mettre à jour leur configuration de sécurité, mais ils n&#39;ont pas accès à d&#39;autres ressources du réseau.</p><h3 id="controles-des-applications-et-autres-changements-de-configuration" tabindex="-1">Contrôles des applications et autres changements de configuration <a class="header-anchor" href="#controles-des-applications-et-autres-changements-de-configuration" aria-label="Permalink to &quot;Contrôles des applications et autres changements de configuration&quot;">​</a></h3><p>Pendant la phase de récupération, les intervenants en cas d&#39;incident peuvent également apporter d&#39;autres modifications de configuration aux dispositifs de sécurité, telles que la mise à jour des règles de pare-feu, la reconfiguration des politiques de gestion des appareils mobiles, la mise en œuvre de technologies de prévention de la perte de données, la mise à jour ou la révocation de certificats numériques, et la mise en œuvre ou la reconfiguration de solutions de filtrage de contenu limitant les ressources Web accessibles par les utilisateurs.</p><h3 id="strategies-de-securite-reseau-segmentation-isolation-et-retrait" tabindex="-1">Stratégies de sécurité réseau: segmentation, isolation et retrait <a class="header-anchor" href="#strategies-de-securite-reseau-segmentation-isolation-et-retrait" aria-label="Permalink to &quot;Stratégies de sécurité réseau: segmentation, isolation et retrait&quot;">​</a></h3><ol><li><em><strong>Segmentation</strong></em>: Divise les réseaux en segments logiques, regroupés par types d&#39;utilisateurs ou de systèmes. Cela permet de contenir la propagation d&#39;une attaque à partir de systèmes compromis sans alerter l&#39;attaquant sur la détection de son activité.</li><li><em><strong>Isolation</strong></em>: Déplace les systèmes compromis vers un réseau complètement déconnecté du reste du réseau.</li><li><em><strong>Retrait</strong></em>: Déconnecte complètement les systèmes impactés de tout réseau.</li></ol><h3 id="technologie-soar-securite-orchestration-automatisation-et-reponse" tabindex="-1">Technologie SOAR (Sécurité, Orchestration, Automatisation et Réponse) <a class="header-anchor" href="#technologie-soar-securite-orchestration-automatisation-et-reponse" aria-label="Permalink to &quot;Technologie SOAR (Sécurité, Orchestration, Automatisation et Réponse)&quot;">​</a></h3><p>Les plates-formes SOAR utilisent des livres d&#39;exécution et des scénarios pour automatiser les réponses aux incidents de sécurité. Elles sont étroitement intégrées aux solutions de gestion des informations et des événements de sécurité (SIEM), de sorte que lorsqu&#39;un produit SIEM détecte un incident de sécurité potentiel, la solution SOAR peut mettre en œuvre automatiquement une réponse rapide qui protège l&#39;organisation contre une attaque.</p><p>Comprendre les techniques d&#39;atténuation et de contrôles pour sécuriser un environnement pendant un incident de sécurité est essentiel pour réussir l&#39;examen Security+. Ces mesures garantissent la restauration des opérations de l&#39;organisation dans un état sécurisé et protègent contre de futures attaques similaires.</p><h2 id="explication-des-aspects-cles-de-la-cyberforensique-objectif-4-5" tabindex="-1">Explication des aspects clés de la cyberforensique (Objectif 4.5) <a class="header-anchor" href="#explication-des-aspects-cles-de-la-cyberforensique-objectif-4-5" aria-label="Permalink to &quot;Explication des aspects clés de la cyberforensique (Objectif 4.5)&quot;">​</a></h2><p>Les professionnels de la sécurité doivent être familiers avec les normes de documentation pour les preuves collectées lors d&#39;une intervention en cas d&#39;incident. Cela inclut la garantie que les preuves collectées seront admissibles en justice, tout en maintenant une chaîne de garde qui documente le processus utilisé pour collecter les preuves et chaque personne ayant eu contact avec ces preuves depuis leur collecte jusqu&#39;à leur présentation en justice.</p><h3 id="elements-cles" tabindex="-1">Éléments clés: <a class="header-anchor" href="#elements-cles" aria-label="Permalink to &quot;Éléments clés:&quot;">​</a></h3><h4 id="_1-chaine-de-garde" tabindex="-1">1. Chaîne de garde: <a class="header-anchor" href="#_1-chaine-de-garde" aria-label="Permalink to &quot;1. Chaîne de garde:&quot;">​</a></h4><ul><li>Documenter le processus de collecte des preuves.</li><li>Identifier chaque personne en contact avec les preuves.</li></ul><h4 id="_2-chronologie-detaillee" tabindex="-1">2. Chronologie détaillée: <a class="header-anchor" href="#_2-chronologie-detaillee" aria-label="Permalink to &quot;2. Chronologie détaillée:&quot;">​</a></h4><ul><li>Séquence d&#39;événements avec horodatage.</li><li>Offset de temps si les horloges système ne sont pas synchronisées.</li></ul><h4 id="_3-attributs-et-etiquetage-des-preuves" tabindex="-1">3. Attributs et étiquetage des preuves: <a class="header-anchor" href="#_3-attributs-et-etiquetage-des-preuves" aria-label="Permalink to &quot;3. Attributs et étiquetage des preuves:&quot;">​</a></h4><ul><li>Attributs importants pour l&#39;enquête.</li><li>Documentation des entretiens, peut-être via des enregistrements vidéo.</li></ul><h4 id="_4-maintien-des-journaux-d-evenements" tabindex="-1">4. Maintien des journaux d&#39;événements: <a class="header-anchor" href="#_4-maintien-des-journaux-d-evenements" aria-label="Permalink to &quot;4. Maintien des journaux d&#39;événements:&quot;">​</a></h4><ul><li>Récapituler les actions lors de l&#39;intervention.</li><li>Documenter ce qui s&#39;est passé pendant l&#39;effort d&#39;intervention.</li></ul><h4 id="_5-procedure-de-conservation-legale" tabindex="-1">5. Procédure de conservation légale: <a class="header-anchor" href="#_5-procedure-de-conservation-legale" aria-label="Permalink to &quot;5. Procédure de conservation légale:&quot;">​</a></h4><ul><li>Préservation des enregistrements pouvant être utilisés en justice.</li></ul><h4 id="_6-ordre-de-volatilite" tabindex="-1">6. Ordre de volatilité: <a class="header-anchor" href="#_6-ordre-de-volatilite" aria-label="Permalink to &quot;6. Ordre de volatilité:&quot;">​</a></h4><ul><li>Collecte des preuves selon le niveau de volatilité.</li><li>Mémoire volatile (RAM) d&#39;abord, puis fichiers sur disque.</li></ul><h4 id="_7-sources-de-preuves" tabindex="-1">7. Sources de preuves: <a class="header-anchor" href="#_7-sources-de-preuves" aria-label="Permalink to &quot;7. Sources de preuves:&quot;">​</a></h4><ul><li>Dispositifs d&#39;extrémité, serveurs, appareils réseau, applications, etc.</li></ul><h4 id="_8-acces-aux-ressources-on-premise-et-dans-le-cloud" tabindex="-1">8. Accès aux ressources On-Premise et dans le Cloud: <a class="header-anchor" href="#_8-acces-aux-ressources-on-premise-et-dans-le-cloud" aria-label="Permalink to &quot;8. Accès aux ressources On-Premise et dans le Cloud:&quot;">​</a></h4><ul><li>Accès potentiellement limité chez les fournisseurs de services cloud.</li><li>Comprendre les capacités et les limites des fournisseurs cloud.</li></ul><h4 id="_9-preservation-de-l-integrite-des-preuves" tabindex="-1">9. Préservation de l&#39;intégrité des preuves: <a class="header-anchor" href="#_9-preservation-de-l-integrite-des-preuves" aria-label="Permalink to &quot;9. Préservation de l&#39;intégrité des preuves:&quot;">​</a></h4><ul><li>Utilisation de fonctions de hachage pour démontrer l&#39;intégrité des fichiers.</li><li>Ajout de signatures numériques pour la non-répudiation.</li></ul><p>La préservation des preuves est cruciale et les intervenants en cas d&#39;incident sont responsables de garantir l&#39;intégrité des preuves collectées. Les mécanismes tels que le hachage et les signatures numériques sont utilisés pour assurer cette intégrité.</p><hr><br><div class="info custom-block"><p class="custom-block-title">Sources</p><p>Ce cours s&#39;inspire du cours sur Linkedin eLearning de <em><strong>Mike CHAPPLE</strong></em> :</p><p><a href="https://www.linkedin.com/learning/comptia-security-plus-sy0-601-cert-prep-1-threats-attacks-and-vulnerabilities?contextUrn=urn%3Ali%3AlyndaLearningPath%3A57bdd64992015ae4c0cb990e&amp;dApp=36524473&amp;leis=LAA&amp;u=98048682" target="_blank" rel="noreferrer">https://www.linkedin.com/learning/comptia-security-plus-sy0-601-cert-prep-1-threats-attacks-and-vulnerabilities?contextUrn=urn%3Ali%3AlyndaLearningPath%3A57bdd64992015ae4c0cb990e&amp;dApp=36524473&amp;leis=LAA&amp;u=98048682</a></p></div>',87))])}const b=i(d,[["render",c]]);export{v as __pageData,b as default};
