import{_ as t,c as a,j as s,a as r,G as l,an as n,B as o,o as u}from"./chunks/framework.ys1W0Hiz.js";const v=JSON.parse('{"title":"Scan des vulnérabilités","description":"","frontmatter":{"sidebar":"auto"},"headers":[],"relativePath":"certification/securityplus/menaces/scan.md","filePath":"certification/securityplus/menaces/scan.md","lastUpdated":1729429463000}'),p={name:"certification/securityplus/menaces/scan.md"};function d(c,e,m,b,g,h){const i=o("Badge");return u(),a("div",null,[e[0]||(e[0]=s("h1",{id:"scan-des-vulnerabilites",tabindex:"-1"},[r("Scan des vulnérabilités "),s("a",{class:"header-anchor",href:"#scan-des-vulnerabilites","aria-label":'Permalink to "Scan des vulnérabilités"'},"​")],-1)),l(i,{type:"tip",text:"Rédigé le 18/10/2024"}),e[1]||(e[1]=n('<h2 id="qu-est-ce-que-la-gestion-des-vulnerabilites" tabindex="-1">Qu&#39;est-ce que la gestion des vulnérabilités ? <a class="header-anchor" href="#qu-est-ce-que-la-gestion-des-vulnerabilites" aria-label="Permalink to &quot;Qu&#39;est-ce que la gestion des vulnérabilités ?&quot;">​</a></h2><h3 id="complexite-des-systemes-modernes" tabindex="-1">Complexité des systèmes modernes <a class="header-anchor" href="#complexite-des-systemes-modernes" aria-label="Permalink to &quot;Complexité des systèmes modernes&quot;">​</a></h3><p>Les systèmes et applications informatiques modernes sont extrêmement compliqués. Il n&#39;est pas surprenant d&#39;apprendre qu&#39;il existe des millions de lignes de code dans chaque logiciel majeur.</p><p><em>Exemple : Le noyau Linux, partie centrale du système d&#39;exploitation, contient plus de 24 millions de lignes de code et change à un rythme étonnant.</em></p><h3 id="processus-de-gestion-des-vulnerabilites" tabindex="-1">Processus de gestion des vulnérabilités <a class="header-anchor" href="#processus-de-gestion-des-vulnerabilites" aria-label="Permalink to &quot;Processus de gestion des vulnérabilités&quot;">​</a></h3><p>Compte tenu de la complexité des logiciels modernes, les développeurs commettent des erreurs qui entraînent des vulnérabilités de sécurité. La communauté de la sécurité a un processus bien établi pour gérer ces vulnérabilités :</p><ol><li><p><strong>Identification de la vulnérabilité</strong> : Lorsqu&#39;une entreprise apprend l&#39;existence d&#39;une vulnérabilité, elle analyse le problème.</p></li><li><p><strong>Développement du correctif</strong> : Une solution (correctif) est développée pour le problème.</p></li><li><p><strong>Diffusion du correctif</strong> : Le correctif est diffusé via le mécanisme de mise à jour de l&#39;entreprise.</p></li><li><p><strong>Application du correctif</strong> : Les administrateurs du monde entier appliquent le correctif pour corriger la vulnérabilité.</p></li></ol><h3 id="travail-des-administrateurs" tabindex="-1">Travail des administrateurs <a class="header-anchor" href="#travail-des-administrateurs" aria-label="Permalink to &quot;Travail des administrateurs&quot;">​</a></h3><p>Pour les administrateurs, la gestion des vulnérabilités représente une charge de travail importante. Les entreprises modernes peuvent exécuter plusieurs systèmes d&#39;exploitation différents et des centaines d&#39;applications, ainsi que des composants comme les routeurs, les commutateurs, les appareils IoT, etc.</p><h3 id="processus-mature-de-gestion-des-vulnerabilites" tabindex="-1">Processus mature de gestion des vulnérabilités <a class="header-anchor" href="#processus-mature-de-gestion-des-vulnerabilites" aria-label="Permalink to &quot;Processus mature de gestion des vulnérabilités&quot;">​</a></h3><p>Un processus mature inclut :</p><ul><li>La recherche de vulnérabilités.</li><li>L&#39;application de correctifs.</li><li>Le suivi des remédiations.</li><li>La génération de rapports sur les résultats.</li></ul><h3 id="motivations-pour-developper-un-programme-de-gestion-des-vulnerabilites" tabindex="-1">Motivations pour développer un programme de gestion des vulnérabilités <a class="header-anchor" href="#motivations-pour-developper-un-programme-de-gestion-des-vulnerabilites" aria-label="Permalink to &quot;Motivations pour développer un programme de gestion des vulnérabilités&quot;">​</a></h3><ul><li><p><strong>Sécuriser les systèmes</strong> : Principal but du programme.</p></li><li><p><strong>Conformité aux politiques de l&#39;entreprise</strong> : Suivi d&#39;un mandat d&#39;entreprise pour gérer les vulnérabilités.</p></li><li><p><strong>Exigences réglementaires</strong> : Conformité à divers règlements de cybersécurité.</p></li></ul><h4 id="exemples-de-reglements" tabindex="-1">Exemples de règlements : <a class="header-anchor" href="#exemples-de-reglements" aria-label="Permalink to &quot;Exemples de règlements :&quot;">​</a></h4><ol><li><p><strong>PCI DSS</strong> : S&#39;applique à toute personne traitant des informations de carte de crédit.</p><ul><li><p>Analyses trimestrielles de vulnérabilités des systèmes et réseaux.</p></li><li><p>Nouvelles analyses après des changements significatifs.</p></li><li><p>Utilisation d&#39;un fournisseur d&#39;analyses approuvé.</p></li><li><p>Remédiation des vulnérabilités et nouvelles analyses jusqu&#39;à l&#39;obtention d&#39;un rapport de santé sans vulnérabilités significatives.</p></li></ul></li><li><p><strong>FISMA (USA)</strong> : Pour les agences gouvernementales américaines.</p><ul><li><p>Recherche régulière de vulnérabilités dans les systèmes et applications.</p></li><li><p>Analyse des résultats des recherches.</p></li><li><p>Remédiation des vulnérabilités jugées légitimes.</p></li><li><p>Partage des informations sur les vulnérabilités avec d&#39;autres agences gouvernementales.</p></li></ul></li></ol><h3 id="mise-en-place-de-la-recherche-de-vulnerabilites" tabindex="-1">Mise en place de la recherche de vulnérabilités <a class="header-anchor" href="#mise-en-place-de-la-recherche-de-vulnerabilites" aria-label="Permalink to &quot;Mise en place de la recherche de vulnérabilités&quot;">​</a></h3><p>Pour mettre en place une recherche de vulnérabilités, il est recommandé de combiner trois types de tests :</p><ol><li><p><strong>Analyses de vulnérabilités réseau</strong> : Pour détecter les problèmes de sécurité des appareils connectés au réseau.</p></li><li><p><strong>Analyses d&#39;applications</strong> : Pour tester le code exécuté sur les appareils et détecter des défauts potentiels.</p></li><li><p><strong>Tests spécialisés pour les applications web</strong> : Pour rechercher des problèmes courants tels que l&#39;injection SQL et le cross-site scripting.</p></li></ol><h3 id="interpretation-des-resultats" tabindex="-1">Interprétation des résultats <a class="header-anchor" href="#interpretation-des-resultats" aria-label="Permalink to &quot;Interprétation des résultats&quot;">​</a></h3><p>Lors de l&#39;interprétation des résultats des recherches de vulnérabilités, il est recommandé de compléter ces recherches par des examens des configurations des systèmes et applications et des journaux pour vérifier les résultats afin d&#39;éviter les faux positifs et autres erreurs.</p><h3 id="conclusion" tabindex="-1">Conclusion <a class="header-anchor" href="#conclusion" aria-label="Permalink to &quot;Conclusion&quot;">​</a></h3><p>Peu importe pourquoi un programme de gestion des vulnérabilités est en cours de développement, les outils et processus de base sont les mêmes. Avant de commencer, il est important de connaître les règles qui s&#39;appliquent à l&#39;organisation pour concevoir un programme conforme aux exigences.</p><h2 id="identifier-les-cibles-de-scan" tabindex="-1">Identifier les cibles de scan <a class="header-anchor" href="#identifier-les-cibles-de-scan" aria-label="Permalink to &quot;Identifier les cibles de scan&quot;">​</a></h2><h3 id="developper-les-exigences-du-programme" tabindex="-1">Développer les exigences du programme <a class="header-anchor" href="#developper-les-exigences-du-programme" aria-label="Permalink to &quot;Développer les exigences du programme&quot;">​</a></h3><p>En démarrant un programme de gestion des vulnérabilités, la première étape consiste à développer des exigences pour ce programme. Il faut réfléchir si le programme est basé sur un désir général d&#39;améliorer la sécurité, une réponse aux exigences réglementaires ou une réaction à la politique de l&#39;entreprise.</p><h3 id="transformation-des-exigences-en-liste-de-systemes" tabindex="-1">Transformation des exigences en liste de systèmes <a class="header-anchor" href="#transformation-des-exigences-en-liste-de-systemes" aria-label="Permalink to &quot;Transformation des exigences en liste de systèmes&quot;">​</a></h3><p>Une fois cela fait, l&#39;étape suivante est de transformer ces exigences générales en une liste des systèmes et réseaux spécifiques à scanner. Pour créer cette liste, il est nécessaire de disposer d&#39;un inventaire des actifs fiable.</p><ul><li><p>Si l&#39;organisation pratique déjà une bonne gestion des actifs, cet inventaire est peut-être déjà prêt à être intégré dans le programme de gestion des vulnérabilités.</p></li><li><p>Les outils de gestion de la configuration de l&#39;organisation peuvent disposer d&#39;un inventaire complet des systèmes et des appareils sur le réseau, mis à jour grâce aux analyses régulières de découverte réseau.</p></li><li><p>Si cela n&#39;est pas possible, il peut être nécessaire de se tourner vers une analyse effectuée par la solution de gestion des vulnérabilités.</p></li></ul><h3 id="execution-des-analyses-de-decouverte-des-hotes" tabindex="-1">Exécution des analyses de découverte des hôtes <a class="header-anchor" href="#execution-des-analyses-de-decouverte-des-hotes" aria-label="Permalink to &quot;Exécution des analyses de découverte des hôtes&quot;">​</a></h3><p>Plutôt que de lancer une analyse complète des vulnérabilités, ce qui peut être très long, le système permet probablement de lancer une analyse légère qui recherche simplement les systèmes sur le réseau local.</p><p>Exemple avec <strong>Nessus</strong> :</p><ol><li><p>Cliquer sur &quot;<code>host directory</code>&quot;.</p></li><li><p>Donner un nom à l&#39;analyse (par exemple, &quot;<em>My Internal Network</em>&quot;).</p></li><li><p>Fournir les cibles de l&#39;analyse en utilisant les adresses IP privées des systèmes sur ce réseau (<em>172.31.0.0/16</em>).</p></li><li><p>Cliquer sur le bouton &quot;Save&quot;.</p></li><li><p>Lancer l&#39;analyse en cliquant sur le bouton &quot;Launch&quot;.</p></li></ol><h3 id="analyse-des-resultats" tabindex="-1">Analyse des résultats <a class="header-anchor" href="#analyse-des-resultats" aria-label="Permalink to &quot;Analyse des résultats&quot;">​</a></h3><p>Au fur et à mesure que l&#39;analyse se déroule, une liste des hôtes apparaissant sur le réseau est remplie. Ces hôtes peuvent ensuite être analysés pour des vulnérabilités supplémentaires en utilisant des analyses de vulnérabilités plus avancées.</p><p><em>Exemple avec <em><strong>Qualys</strong></em> : Une vue graphique des résultats de la découverte réseau peut être fournie.</em></p><h3 id="priorisation-des-actifs-pour-les-analyses" tabindex="-1">Priorisation des actifs pour les analyses <a class="header-anchor" href="#priorisation-des-actifs-pour-les-analyses" aria-label="Permalink to &quot;Priorisation des actifs pour les analyses&quot;">​</a></h3><p>Une fois un inventaire des actifs solide en place, il est nécessaire de commencer à prioriser ces actifs pour les analyses. Cela se fait en répondant à trois questions clés sur chaque actif.</p><ol><li><p>Importance du système :</p><ul><li><p>Impact en cas de violation.</p></li><li><p>Niveau de classification des données stockées, traitées ou transmises par le système.</p></li></ul></li></ol><p><em>Exemple : Un système traitant des informations sensibles aura une priorité plus élevée.</em></p><ol start="2"><li><p>Niveau de risque :</p><ul><li><p>Probabilité d&#39;une attaque réussie.</p></li><li><p>Exposition réseau (accessible depuis l&#39;internet public ? Derrière un pare-feu ?).</p></li></ul></li></ol><p><em>Exemple : Un serveur web accessible publiquement est plus susceptible d&#39;être attaqué qu&#39;un serveur interne.</em></p><ol start="3"><li><p>Criticité du système :</p><ul><li><p>Importance pour les opérations.</p></li><li><p>Impact sur les opérations commerciales si le système n&#39;est pas disponible.</p></li></ul></li></ol><p><em>Exemple : Un système critique pour les opérations commerciales sera priorisé par rapport à un système non critique.</em></p><h3 id="scan-de-tous-les-systemes" tabindex="-1">Scan de tous les systèmes <a class="header-anchor" href="#scan-de-tous-les-systemes" aria-label="Permalink to &quot;Scan de tous les systèmes&quot;">​</a></h3><p>De nombreuses organisations adoptent l&#39;approche de scanner régulièrement tous les systèmes, appareils et applications dans leur environnement. Cela est acceptable mais n&#39;élimine pas le besoin de réaliser un inventaire des actifs et d&#39;identifier la criticité des différentes ressources.</p><p>Exemple : Même en scannant tout, il est nécessaire de prioriser les efforts de remédiation.</p><h2 id="configuration-du-scan" tabindex="-1">Configuration du Scan <a class="header-anchor" href="#configuration-du-scan" aria-label="Permalink to &quot;Configuration du Scan&quot;">​</a></h2><h3 id="creer-un-nouveau-scan" tabindex="-1">Créer un nouveau scan <a class="header-anchor" href="#creer-un-nouveau-scan" aria-label="Permalink to &quot;Créer un nouveau scan&quot;">​</a></h3><p>Nous venons de réaliser un simple scan de vulnérabilité, mais explorons maintenant le processus de configuration d&#39;un scan en détail.</p><ol><li><p>Nouveau scan :</p><ul><li><p>Cliquer sur le bouton &quot;New Scan&quot;.</p></li><li><p>Choisir parmi une série de modèles préconfigurés (ou sélectionner &quot;advanced scan&quot; pour choisir ses propres paramètres de scan).</p></li></ul></li></ol><p><em>Exemple : Configurer un scan appelé &quot;Mike&#39;s Scan&quot;.</em></p><ol start="2"><li><p>Paramètres de base :</p><ul><li><p>Nommer le scan.</p></li><li><p>Décrire le scan (optionnel).</p></li><li><p><strong>Boîte de cibles</strong> : Configurer la portée du scan en entrant les noms, adresses IP ou plages de réseaux.</p></li></ul></li></ol><p><em>Exemple : Scanner tous les systèmes dans le réseau 172.30.0.0/24.</em></p><ol start="3"><li><p>Fichier de cibles :</p><ul><li>Télécharger un fichier de cibles si l&#39;organisation dispose d&#39;un outil de gestion des actifs séparé.</li></ul></li></ol><h3 id="organiser-le-programme-de-scan" tabindex="-1">Organiser le programme de scan <a class="header-anchor" href="#organiser-le-programme-de-scan" aria-label="Permalink to &quot;Organiser le programme de scan&quot;">​</a></h3><ul><li><p>Fréquence de scan : Organiser les scans en fonction de la fréquence souhaitée (quotidienne, hebdomadaire, etc.).</p></li><li><p>Exemples :</p><ul><li><p>Scanner les systèmes traitant des informations confidentielles, sensibles et très sensibles séparément.</p></li><li><p>Configurer le scan pour qu&#39;il s&#39;exécute du lundi au vendredi à une heure spécifique.</p></li></ul></li></ul><h3 id="notifications" tabindex="-1">Notifications <a class="header-anchor" href="#notifications" aria-label="Permalink to &quot;Notifications&quot;">​</a></h3><ul><li>Destinataires des rapports : Configurer les destinataires des rapports de scan par e-mail une fois le scan terminé.</li></ul><h3 id="parametres-techniques" tabindex="-1">Paramètres techniques <a class="header-anchor" href="#parametres-techniques" aria-label="Permalink to &quot;Paramètres techniques&quot;">​</a></h3><ol><li><p>Découverte des hôtes :</p><ul><li><p>Configurer les types de pings réseau.</p></li><li><p>Gérer les appareils comme les imprimantes et les systèmes Netware.</p></li></ul></li><li><p>Scannage des ports :</p><ul><li><p>Configurer les ports spécifiques à scanner.</p></li><li><p>Définir les protocoles à utiliser pour la détection des ports ouverts.</p></li></ul></li></ol><p>Par défaut, Nessus inclut tous les ports couramment utilisés, mais il est possible de configurer des ports personnalisés.</p><ol start="3"><li><p>Sensibilité du scan :</p><ul><li>Niveau de sensibilité : Configurer le niveau de sensibilité du scan pour équilibrer le risque de fausses alertes et de vulnérabilités manquées.</li></ul></li></ol><p><em>Exemple : Utiliser &quot;normal accuracy&quot; par défaut, ou configurer pour montrer ou éviter les fausses alertes.</em></p><ol start="4"><li><p>Paramètres avancés :</p><ul><li><p><strong>Safe checks</strong> : Activer pour éviter de perturber les systèmes.</p></li><li><p><strong>Performance du scan</strong> : Ralentir le scan en cas de congestion réseau, définir des délais spécifiques et des contrôles pour limiter le taux de scan.</p></li></ul></li></ol><h3 id="plugins" tabindex="-1">Plugins <a class="header-anchor" href="#plugins" aria-label="Permalink to &quot;Plugins&quot;">​</a></h3><p>Nessus utilise des plugins pour effectuer des vérifications de vulnérabilités. Chaque plugin est conçu pour vérifier une vulnérabilité spécifique, et ils sont organisés par type de systèmes affectés.</p><h2 id="perspective-du-scan" tabindex="-1">Perspective du Scan <a class="header-anchor" href="#perspective-du-scan" aria-label="Permalink to &quot;Perspective du Scan&quot;">​</a></h2><h3 id="differents-types-de-scans-de-vulnerabilites" tabindex="-1">Différents types de scans de vulnérabilités <a class="header-anchor" href="#differents-types-de-scans-de-vulnerabilites" aria-label="Permalink to &quot;Différents types de scans de vulnérabilités&quot;">​</a></h3><p>Tous les scans de vulnérabilités ne sont pas identiques. Bien qu&#39;ils puissent tester les mêmes systèmes avec les mêmes outils, ports et services, d&#39;autres facteurs peuvent influencer les résultats des scans. Parlons de la perspective du scan.</p><h3 id="importance-de-la-localisation-du-scanner" tabindex="-1">Importance de la localisation du scanner <a class="header-anchor" href="#importance-de-la-localisation-du-scanner" aria-label="Permalink to &quot;Importance de la localisation du scanner&quot;">​</a></h3><p>La localisation du scanner sur le réseau par rapport aux systèmes scannés est cruciale :</p><ol><li><p>Scanner dans la DMZ :</p><ul><li><p>Accès non restreint au serveur web.</p></li><li><p>Pas besoin de passer par le pare-feu.</p></li></ul></li><li><p>Scanner dans le réseau interne :</p><ul><li><p>Le trafic du scanner doit passer par le pare-feu.</p></li><li><p>Le pare-feu peut bloquer les connexions non autorisées et filtrer le trafic suspect.</p></li></ul></li><li><p>Scanner sur internet :</p><ul><li><p>Le trafic doit passer par le pare-feu avec des règles plus strictes pour le trafic entrant.</p></li><li><p>Moins de vulnérabilités détectées en raison des règles de pare-feu plus strictes.</p></li></ul></li></ol><h3 id="quelle-perspective-est-correcte" tabindex="-1">Quelle perspective est correcte ? <a class="header-anchor" href="#quelle-perspective-est-correcte" aria-label="Permalink to &quot;Quelle perspective est correcte ?&quot;">​</a></h3><p>Chaque perspective est correcte et offre une vue différente :</p><ul><li><p><strong>Scanner dans la DMZ</strong> : Offre une vue claire des vulnérabilités du système cible.</p></li><li><p><strong>Scanner sur internet</strong> : Offre la perspective d&#39;un attaquant externe, aidant à prioriser les efforts de remédiation.</p></li></ul><h3 id="impact-des-pare-feu-et-systemes-de-prevention-des-intrusions" tabindex="-1">Impact des pare-feu et systèmes de prévention des intrusions <a class="header-anchor" href="#impact-des-pare-feu-et-systemes-de-prevention-des-intrusions" aria-label="Permalink to &quot;Impact des pare-feu et systèmes de prévention des intrusions&quot;">​</a></h3><ul><li><p>Les paramètres du pare-feu influencent les scans de vulnérabilités en modifiant la segmentation des systèmes et services visibles.</p></li><li><p>Les systèmes de prévention des intrusions (IPS) actifs sur le réseau affectent également les résultats des scans.</p></li></ul><h3 id="scans-bases-sur-le-serveur-et-scans-bases-sur-l-agent" tabindex="-1">Scans basés sur le serveur et scans basés sur l&#39;agent <a class="header-anchor" href="#scans-bases-sur-le-serveur-et-scans-bases-sur-l-agent" aria-label="Permalink to &quot;Scans basés sur le serveur et scans basés sur l&#39;agent&quot;">​</a></h3><ol><li><p>Scans basés sur le serveur :</p><ul><li>Le scanner de vulnérabilités se connecte à un système via le réseau et le sonde pour détecter les vulnérabilités.</li></ul></li><li><p>Scans basés sur l&#39;agent :</p><ul><li><p>Un agent de sécurité est installé sur chaque serveur, sonde la configuration du serveur et vérifie les vulnérabilités.</p></li><li><p>Les agents rapportent les faiblesses découvertes au système central de gestion des vulnérabilités.</p></li><li><p>Certaines organisations évitent cette méthode pour ne pas augmenter la complexité de leur environnement.</p></li></ul></li></ol><h3 id="scans-bases-sur-les-identifiants" tabindex="-1">Scans basés sur les identifiants <a class="header-anchor" href="#scans-bases-sur-les-identifiants" aria-label="Permalink to &quot;Scans basés sur les identifiants&quot;">​</a></h3><p>Une alternative aux scans basés sur l&#39;agent est le scan basé sur les identifiants :</p><ol><li><p>Configurer les scans basés sur les identifiants dans Nessus :</p><ul><li><p>Choisir l&#39;onglet &quot;credentials&quot; dans les paramètres du scan.</p></li><li><p>Configurer les identifiants SSH ou Windows.</p></li><li><p>Remplir le nom d&#39;utilisateur, le mot de passe ou la clé privée associée.</p></li></ul></li></ol><p><em>Bonnes pratiques</em> : Ne pas fournir de compte administratif au scanner, mais plutôt un compte avec un accès en lecture seule à la configuration du système.</p><h2 id="security-content-automation-protocol-scap" tabindex="-1">Security Content Automation Protocol (<em><strong>SCAP</strong></em>) <a class="header-anchor" href="#security-content-automation-protocol-scap" aria-label="Permalink to &quot;Security Content Automation Protocol (***SCAP***)&quot;">​</a></h2><h3 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-label="Permalink to &quot;Introduction&quot;">​</a></h3><p>Le monde de la gestion des vulnérabilités est rempli de jargon, rendant parfois les choses confuses. Les termes &quot;vulnérabilité des applications web&quot;, &quot;problème d&#39;injection SQL&quot; et &quot;défaut de validation des entrées&quot; peuvent tous désigner la même chose. De plus, les vulnérabilités peuvent être qualifiées de &quot;sévères&quot;, &quot;critiques&quot; ou &quot;urgentes&quot;, ajoutant à l&#39;ambiguïté. Cette confusion peut empêcher l&#39;automatisation des activités de gestion des vulnérabilités, comme si les systèmes ne parlaient pas la même langue.</p><h3 id="le-role-du-scap" tabindex="-1">Le rôle du SCAP <a class="header-anchor" href="#le-role-du-scap" aria-label="Permalink to &quot;Le rôle du SCAP&quot;">​</a></h3><p>Le <strong>Security Content Automation Protocol</strong> (<em><strong>SCAP</strong></em>) intervient ici. C&#39;est un effort dirigé par le National Institute for Standards and Technology (NIST) pour créer un langage et un format cohérents pour discuter des problèmes de sécurité. Les systèmes conformes aux standards SCAP peuvent partager des informations de manière cohérente pour décrire les environnements, les vulnérabilités et les étapes de remédiation.</p><h3 id="les-composants-du-scap" tabindex="-1">Les composants du SCAP <a class="header-anchor" href="#les-composants-du-scap" aria-label="Permalink to &quot;Les composants du SCAP&quot;">​</a></h3><p>Voici un aperçu des composants du SCAP :</p><ol><li><p>Common Vulnerability Scoring System (CVSS) :</p><ul><li><p>Utilisé dans toute la communauté de la sécurité pour évaluer la gravité des vulnérabilités de sécurité.</p></li><li><p>Les scores CVSS sont présents dans la plupart des produits de scan de vulnérabilités et dans les rapports de scan.</p></li></ul></li><li><p>Common Configuration Enumeration (CCE) :</p><ul><li>Offre un langage cohérent pour partager les configurations de système.</li></ul></li><li><p>Common Platform Enumeration (CPE) :</p><ul><li>Standardise les noms et les versions des produits.</li></ul></li><li><p>Common Vulnerabilities and Exposures (CVE) :</p><ul><li>Fournit un langage pour décrire les vulnérabilités.</li></ul></li><li><p>Extensible Configuration Checklist Description Format (XCCDF) :</p><ul><li>Fournit un langage pour créer et partager des listes de contrôle et les résultats de traitement des listes de contrôle de sécurité.</li></ul></li><li><p>Open Vulnerability and Assessment Language (OVAL) :</p><ul><li>Décrit les procédures de test de manière programmatique.</li></ul></li></ol><h3 id="detail-sur-le-cvss" tabindex="-1">Détail sur le CVSS <a class="header-anchor" href="#detail-sur-le-cvss" aria-label="Permalink to &quot;Détail sur le CVSS&quot;">​</a></h3><p>Parmi ces composants, le <strong>Common Vulnerability Scoring System</strong> (<em><strong>CVSS</strong></em>) est particulièrement utilisé car il offre une méthode cohérente pour évaluer la gravité des vulnérabilités de sécurité.</p><h2 id="cvss-common-vulnerability-scoring-system" tabindex="-1">CVSS (<em>Common Vulnerability Scoring System</em>) <a class="header-anchor" href="#cvss-common-vulnerability-scoring-system" aria-label="Permalink to &quot;CVSS (*Common Vulnerability Scoring System*)&quot;">​</a></h2><h3 id="introduction-1" tabindex="-1">Introduction <a class="header-anchor" href="#introduction-1" aria-label="Permalink to &quot;Introduction&quot;">​</a></h3><p>Le <strong>Common Vulnerability Scoring System</strong> (<em><strong>CVSS</strong></em>) attribue un score à chaque vulnérabilité sur une échelle de 10 points. Ce score de base est déterminé en évaluant huit métriques différentes et en combinant les résultats.</p><h3 id="metriques-du-cvss" tabindex="-1">Métriques du CVSS <a class="header-anchor" href="#metriques-du-cvss" aria-label="Permalink to &quot;Métriques du CVSS&quot;">​</a></h3><ol><li><p>Vecteur d&#39;attaque (Attack Vector) :</p><ul><li><p>Type d&#39;accès requis par un attaquant pour exploiter une vulnérabilité.</p></li><li><p>Valeurs possibles :</p><ul><li><p><strong>Physical</strong>: L&#39;attaquant doit toucher ou manipuler physiquement le système cible.</p></li><li><p><strong>Local</strong>: L&#39;attaquant doit avoir un accès physique ou logique à la console du système.</p></li><li><p><strong>Adjacent Network</strong>: L&#39;attaquant doit avoir accès au réseau local du système.</p></li><li><p><strong>Network</strong>: La vulnérabilité est exploitable à distance.</p></li></ul></li></ul></li><li><p>Complexité de l&#39;attaque (Attack Complexity) :</p><ul><li><p>Difficulté d&#39;exploitation d&#39;une vulnérabilité.</p></li><li><p>Valeurs possibles :</p><ul><li><p><strong>High</strong>: Conditions spécialisées et travail difficile requis.</p></li><li><p><strong>Low</strong>: Facile à exploiter.</p></li></ul></li></ul></li><li><p>Privilèges requis (Privileges Required) :</p><ul><li><p>Niveau d&#39;accès utilisateur nécessaire pour exploiter la vulnérabilité.</p></li><li><p>Valeurs possibles :</p><ul><li><p><strong>High</strong>: L&#39;attaque nécessite des privilèges administratifs.</p></li><li><p><strong>Low</strong>: L&#39;attaque nécessite l&#39;utilisation d&#39;un compte utilisateur de base.</p></li><li><p><strong>None</strong>: L&#39;attaque peut être exploitée sans aucun accès préalable au système.</p></li></ul></li></ul></li><li><p>Interaction utilisateur (User Interaction) :</p><ul><li><p>Niveau d&#39;interaction humaine nécessaire.</p></li><li><p>Valeurs possibles :</p><ul><li><p><strong>Required</strong>: L&#39;attaquant doit inciter un utilisateur autorisé à effectuer une action.</p></li><li><p><strong>None</strong>: L&#39;attaquant peut réaliser l&#39;attaque seul.</p></li></ul></li></ul></li></ol><h3 id="impact-des-vulnerabilites" tabindex="-1">Impact des vulnérabilités <a class="header-anchor" href="#impact-des-vulnerabilites" aria-label="Permalink to &quot;Impact des vulnérabilités&quot;">​</a></h3><p>Les trois éléments de la <strong>triade CIA</strong> (<em><strong>Confidentialité, Intégrité, Disponibilité</strong></em>) sont utilisés pour mesurer l&#39;impact d&#39;une vulnérabilité :</p><ol><li><p>Confidentialité (Confidentiality) :</p><ul><li><p>Valeurs possibles :</p><ul><li><p><strong>None</strong>: Aucun impact sur la confidentialité.</p></li><li><p><strong>Partial</strong>: Accès à une partie des informations.</p></li><li><p><strong>High</strong>: Toutes les informations du système sont compromises.</p></li></ul></li></ul></li><li><p>Intégrité (Integrity) :</p><ul><li><p>Valeurs possibles :</p><ul><li><p><strong>None</strong>: Aucun impact sur l&#39;intégrité.</p></li><li><p><strong>Low</strong>: Modification possible de certaines informations.</p></li><li><p><strong>High</strong>: Toutes les informations peuvent être modifiées à volonté.</p></li></ul></li></ul></li><li><p>Disponibilité (Availability) :</p><ul><li><p>Valeurs possibles :</p><ul><li><p><strong>None</strong>: Aucun impact sur la disponibilité.</p></li><li><p><strong>Low</strong>: Dégradation des performances.</p></li><li><p><strong>High</strong>: Arrêt complet du système cible.</p></li></ul></li></ul></li></ol><h3 id="portee-scope" tabindex="-1">Portée (Scope) : <a class="header-anchor" href="#portee-scope" aria-label="Permalink to &quot;Portée (Scope) :&quot;">​</a></h3><p>Capturer si une vulnérabilité peut affecter des composants autres que le composant vulnérable :</p><ul><li><p><strong>Changed</strong>: La vulnérabilité peut affecter des ressources au-delà de son périmètre initial.</p></li><li><p><strong>Unchanged</strong>: L&#39;exploit ne peut affecter que les ressources gérées par la même autorité de sécurité.</p></li></ul><h2 id="analyse-des-rapports-de-scan" tabindex="-1">Analyse des rapports de scan <a class="header-anchor" href="#analyse-des-rapports-de-scan" aria-label="Permalink to &quot;Analyse des rapports de scan&quot;">​</a></h2><h3 id="role-de-l-analyste-en-cybersecurite" tabindex="-1">Rôle de l&#39;analyste en cybersécurité <a class="header-anchor" href="#role-de-l-analyste-en-cybersecurite" aria-label="Permalink to &quot;Rôle de l&#39;analyste en cybersécurité&quot;">​</a></h3><p>En tant qu&#39;analyste en cybersécurité, une grande partie du temps est consacrée à l&#39;analyse des rapports de scans de vulnérabilités. Les responsabilités incluent :</p><ul><li><p>Tri des résultats des scans</p></li><li><p>Présentation de l&#39;information à divers publics :</p><ul><li><p><strong>Ingénieurs, développeurs et administrateurs systèmes</strong> : Fournir les détails techniques nécessaires pour corriger les problèmes.</p></li><li><p><strong>Leaders d&#39;entreprise</strong> : Expliquer les tendances et les évaluations des risques élevés.</p></li><li><p><strong>Gestion de la sécurité</strong> : Présenter une vue d&#39;ensemble de la gestion des risques de l&#39;organisation.</p></li></ul></li></ul><h3 id="facteurs-cles-lors-de-l-interpretation-des-rapports" tabindex="-1">Facteurs clés lors de l&#39;interprétation des rapports <a class="header-anchor" href="#facteurs-cles-lors-de-l-interpretation-des-rapports" aria-label="Permalink to &quot;Facteurs clés lors de l&#39;interprétation des rapports&quot;">​</a></h3><p>Cinq facteurs principaux à considérer :</p><ol><li><p>Gravité de la vulnérabilité :</p><ul><li>Évaluer la sévérité.</li></ul></li><li><p>Criticité des systèmes affectés :</p><ul><li>Importance des systèmes touchés.</li></ul></li><li><p>Sensibilité des informations impliquées :</p><ul><li>Niveau de sensibilité des données concernées.</li></ul></li><li><p>Difficulté de remédiation :</p><ul><li>Complexité de la correction de la vulnérabilité.</li></ul></li><li><p>Exposition du système :</p><ul><li>Niveau d&#39;exposition du système vulnérable.</li></ul></li></ol><h3 id="validation-des-vulnerabilites" tabindex="-1">Validation des vulnérabilités <a class="header-anchor" href="#validation-des-vulnerabilites" aria-label="Permalink to &quot;Validation des vulnérabilités&quot;">​</a></h3><p>Avant de demander la remédiation, il est crucial de valider la vulnérabilité. Cela implique :</p><ul><li><p>Vérification de l&#39;existence de la vulnérabilité :</p><ul><li><p>Les scanners de vulnérabilités peuvent générer des faux positifs pour diverses raisons.</p></li><li><p>Vérifier que la signature utilisée par le scanner est bien définie.</p></li><li><p>Vérifier que le scanner peut détecter les contrôles de sécurité présents.</p></li></ul></li><li><p>Revue des détails du rapport de scan :</p><ul><li><p>Analyser les entrées et sorties fournies par le scanner.</p></li><li><p>Comprendre pourquoi le scanner a signalé une vulnérabilité.</p></li></ul></li></ul><p><em>Exemple : Un rapport de scan montrant une vulnérabilité critique dans la version du noyau Linux Ubuntu sur un hôte réseau avec un score CVSS de 10.0. Vérifier les alertes et confirmer que le système exécute une version affectée.</em></p><h3 id="detection-des-faux-positifs-et-des-exceptions" tabindex="-1">Détection des faux positifs et des exceptions <a class="header-anchor" href="#detection-des-faux-positifs-et-des-exceptions" aria-label="Permalink to &quot;Détection des faux positifs et des exceptions&quot;">​</a></h3><ul><li><p><strong>Faux positifs</strong> : Parfois, les faux positifs sont faciles à identifier. Par exemple, un rapport indiquant qu&#39;un serveur Windows manque un patch Mac est probablement un faux positif.</p></li><li><p><strong>Exceptions</strong> : Certaines vulnérabilités peuvent être reconnues par l&#39;organisation, avec des contrôles compensatoires en place ou une acceptation du risque.</p></li></ul><h3 id="consequences-de-la-validation-incorrecte" tabindex="-1">Conséquences de la validation incorrecte <a class="header-anchor" href="#consequences-de-la-validation-incorrecte" aria-label="Permalink to &quot;Conséquences de la validation incorrecte&quot;">​</a></h3><ul><li><p>Détecter les faux positifs et les exceptions est crucial avant d&#39;escalader les vulnérabilités pour remédiation.</p></li><li><p>Risque de perdre en crédibilité si les rapports de vulnérabilités sont mal validés.</p></li></ul><h3 id="types-de-resultats-des-rapports-de-vulnerabilites" tabindex="-1">Types de résultats des rapports de vulnérabilités <a class="header-anchor" href="#types-de-resultats-des-rapports-de-vulnerabilites" aria-label="Permalink to &quot;Types de résultats des rapports de vulnérabilités&quot;">​</a></h3><p>Il existe quatre résultats possibles pour tout rapport de vulnérabilité :</p><ol><li><p><strong>True Positive</strong> : La vulnérabilité existe réellement.</p></li><li><p><strong>False Positive</strong> : La vulnérabilité n&#39;existe pas réellement.</p></li><li><p><strong>True Negative</strong> : Pas de vulnérabilité et pas de détection.</p></li><li><p><strong>False Negative</strong> : La vulnérabilité existe mais n&#39;a pas été détectée.</p></li></ol><h2 id="correlation-des-resultats-de-scan" tabindex="-1">Correlation des Résultats de Scan <a class="header-anchor" href="#correlation-des-resultats-de-scan" aria-label="Permalink to &quot;Correlation des Résultats de Scan&quot;">​</a></h2><h3 id="validation-et-elimination-des-faux-positifs" tabindex="-1">Validation et élimination des faux positifs <a class="header-anchor" href="#validation-et-elimination-des-faux-positifs" aria-label="Permalink to &quot;Validation et élimination des faux positifs&quot;">​</a></h3><p>En plus de valider les résultats des scans pour éliminer les faux positifs et enlever les exceptions documentées, il est essentiel de corréler les rapports de scan avec d&#39;autres informations disponibles à partir de diverses sources.</p><h3 id="sources-d-informations-a-consulter" tabindex="-1">Sources d&#39;informations à consulter <a class="header-anchor" href="#sources-d-informations-a-consulter" aria-label="Permalink to &quot;Sources d&#39;informations à consulter&quot;">​</a></h3><ol><li><p><strong>Standards de l&#39;industrie, meilleures pratiques et obligations de conformité</strong> :</p><ul><li>Ces standards fournissent des directives spécifiques sur les types de vulnérabilités nécessitant une remédiation urgente.</li></ul></li></ol><p><em>Exemple : PCI DSS : Pour démontrer la conformité, un scan ne doit contenir aucune vulnérabilité de haut niveau dans les composants de l&#39;environnement des données de carte. Pour être conforme, aucun de ces composants ne doit contenir une vulnérabilité avec un score CVSS égal ou supérieur à 4.0.</em></p><ol start="2"><li><p><strong>Informations techniques internes</strong> :</p><ul><li>Systèmes de gestion de configuration, référentiels de journaux et autres sources de données peuvent aider à détecter et éliminer les faux positifs.</li></ul></li><li><p><strong>Corrélation des informations des scans</strong> :</p><ul><li><p>Surveiller les tendances historiques dans les scans.</p></li><li><p>Identifier les problèmes sous-jacents si les mêmes types de vulnérabilités continuent d&#39;apparaître.</p></li></ul></li></ol><p><em>Exemple : Si de nouvelles applications web présentent régulièrement des vulnérabilités de type cross-site scripting, il peut être nécessaire de former les développeurs ou de créer des bibliothèques standard de validation des entrées.</em></p><h3 id="conclusion-1" tabindex="-1">Conclusion <a class="header-anchor" href="#conclusion-1" aria-label="Permalink to &quot;Conclusion&quot;">​</a></h3><p>Corréler les résultats des scans avec d&#39;autres informations permet de mieux gérer les vulnérabilités et d&#39;adresser les causes sous-jacentes, réduisant ainsi le besoin de remédiation ultérieure.</p><hr><br><div class="info custom-block"><p class="custom-block-title">Sources</p><p>Ce cours s&#39;inspire du cours sur Linkedin eLearning de <em><strong>Mike CHAPPLE</strong></em> :</p><p><a href="https://www.linkedin.com/learning/comptia-security-plus-sy0-601-cert-prep-1-threats-attacks-and-vulnerabilities?contextUrn=urn%3Ali%3AlyndaLearningPath%3A57bdd64992015ae4c0cb990e&amp;dApp=36524473&amp;leis=LAA&amp;u=98048682" target="_blank" rel="noreferrer">https://www.linkedin.com/learning/comptia-security-plus-sy0-601-cert-prep-1-threats-attacks-and-vulnerabilities?contextUrn=urn%3Ali%3AlyndaLearningPath%3A57bdd64992015ae4c0cb990e&amp;dApp=36524473&amp;leis=LAA&amp;u=98048682</a></p></div>',136))])}const q=t(p,[["render",d]]);export{v as __pageData,q as default};
