import{_ as t,E as i,c as a,J as n,m as e,a as r,a5 as o,o as l}from"./chunks/framework.Xu8UMlTK.js";const y=JSON.parse('{"title":"Menaces, attaques, et vulnérabilités","description":"","frontmatter":{"sidebar":"auto"},"headers":[],"relativePath":"certification/securityplus/tav.md","filePath":"certification/securityplus/tav.md","lastUpdated":1711303250000}'),u={name:"certification/securityplus/tav.md"},d=e("h1",{id:"menaces-attaques-et-vulnerabilites",tabindex:"-1"},[r("Menaces, attaques, et vulnérabilités "),e("a",{class:"header-anchor",href:"#menaces-attaques-et-vulnerabilites","aria-label":'Permalink to "Menaces, attaques, et vulnérabilités"'},"​")],-1),c=o('<h2 id="ingenierie-sociale-objectif-1-1" tabindex="-1">Ingénierie sociale (Objectif 1.1) <a class="header-anchor" href="#ingenierie-sociale-objectif-1-1" aria-label="Permalink to &quot;Ingénierie sociale (Objectif 1.1)&quot;">​</a></h2><h3 id="techniques-d-ingenierie-sociale-distante" tabindex="-1">Techniques d&#39;ingénierie sociale distante <a class="header-anchor" href="#techniques-d-ingenierie-sociale-distante" aria-label="Permalink to &quot;Techniques d&#39;ingénierie sociale distante&quot;">​</a></h3><ol><li><p><em><strong>Phishing</strong></em> : Envoi d&#39;emails trompeurs pour inciter les destinataires à divulguer des informations sensibles ou à effectuer certaines actions.</p></li><li><p><em><strong>SMShing</strong></em> : Similaire au phishing mais utilisant des messages texte au lieu d&#39;emails.</p></li><li><p><em><strong>Vishing</strong></em> : Phishing vocal, où les attaquants utilisent des appels téléphoniques pour tromper les victimes afin d&#39;obtenir des informations sensibles.</p></li><li><p><em><strong>Spear Phishing</strong></em> : Attaques de phishing ciblées sur des individus ou des groupes spécifiques, en exploitant des connaissances internes.</p></li><li><p><em><strong>Whaling</strong></em> : Cibler des personnalités ou des cadres de haut niveau pour des attaques de phishing.</p></li><li><p><em><strong>Vol d&#39;identifiants</strong></em> : Obtention d&#39;identifiants, de mots de passe ou d&#39;autres informations d&#39;identification par des moyens trompeurs.</p></li><li><p><em><strong>Arnaques aux factures</strong></em> : Envoi de fausses factures pour tromper des individus ou des organisations afin de les inciter à effectuer des paiements.</p></li><li><p><em><strong>Spam</strong></em> : Emails commerciaux non sollicités, souvent à des fins de marketing.</p></li><li><p><em><strong>SPIM</strong></em> (Spam via Messagerie Instantanée) : Messages indésirables envoyés via des plateformes de messagerie instantanée.</p></li></ol><h3 id="techniques-d-ingenierie-sociale-physique" tabindex="-1">Techniques d&#39;ingénierie sociale physique : <a class="header-anchor" href="#techniques-d-ingenierie-sociale-physique" aria-label="Permalink to &quot;Techniques d&#39;ingénierie sociale physique :&quot;">​</a></h3><ol><li><p><em><strong>Épier par-dessus l&#39;épaule</strong></em> : Observer l&#39;écran d&#39;ordinateur ou les activités de quelqu&#39;un de près.</p></li><li><p><em><strong>Plongée dans les Poubelles</strong></em> : Recherche à travers les matériaux jetés, tels que les documents papier, pour recueillir des informations sensibles.</p></li><li><p><em><strong>Suivi</strong></em> : Obtenir un accès physique non autorisé à un bâtiment en suivant de près une personne autorisée.</p></li></ol><h3 id="principes-exploites-dans-les-attaques-d-ingenierie-sociale" tabindex="-1">Principes Exploités dans les Attaques d&#39;Ingénierie Sociale : <a class="header-anchor" href="#principes-exploites-dans-les-attaques-d-ingenierie-sociale" aria-label="Permalink to &quot;Principes Exploités dans les Attaques d&#39;Ingénierie Sociale :&quot;">​</a></h3><ol><li>Autorité</li><li>Intimidation</li><li>Consensus</li><li>Rareté</li><li>Familiarité</li><li>Confiance</li><li>Urgence</li></ol><p>Comprendre ces techniques et principes est crucial pour mettre en œuvre des mesures de sécurité efficaces afin de réduire les risques associés aux attaques d&#39;ingénierie sociale.</p><h2 id="malwares-objectif-1-2" tabindex="-1">Malwares (Objectif 1.2) <a class="header-anchor" href="#malwares-objectif-1-2" aria-label="Permalink to &quot;Malwares (Objectif 1.2)&quot;">​</a></h2><p>Les malwares sont des logiciels malveillants qui peuvent affecter nos organisations en exploitant des vulnérabilités ou en incitant un utilisateur à installer du code malveillant.</p><ol><li><em><strong>Virus</strong></em> : Se propagent d&#39;un système à un autre lorsque l&#39;utilisateur effectue une action.</li><li><em><strong>Ransomware</strong></em> : Chiffre tous les fichiers d&#39;un système et demande une rançon en cryptomonnaie pour fournir la clé de déchiffrement.</li><li><em><strong>Vers</strong></em> (Worms) : Se propagent d&#39;un système à l&#39;autre sans nécessiter d&#39;action de l&#39;utilisateur.</li><li><em><strong>Chevaux de Troie</strong></em> (Trojan horses) : Se font passer pour des applications légitimes mais exécutent des activités malveillantes en arrière-plan.</li><li><em><strong>Bombes logiques</strong></em> (Logic bombs) : Intégrées à d&#39;autres logiciels, elles déclenchent une action malveillante lorsque certaines conditions sont remplies.</li><li><em><strong>Logiciels espions</strong></em> (Spyware) : Visent à espionner les activités de l&#39;utilisateur, comme les enregistreurs de frappe ou les chevaux de Troie à accès distant.</li><li><em><strong>Botnets</strong></em> : Collection de systèmes compromis contrôlés par un attaquant pour obtenir un avantage.</li></ol><h3 id="attaques-de-mots-de-passe" tabindex="-1">Attaques de mots de passe : <a class="header-anchor" href="#attaques-de-mots-de-passe" aria-label="Permalink to &quot;Attaques de mots de passe :&quot;">​</a></h3><p>Les mots de passe sont vulnérables à différentes attaques :</p><ol><li><em><strong>Attaques par devinettes</strong></em> (Brute force) : Tentatives de deviner toutes les combinaisons possibles de mots de passe.</li><li><em><strong>Attaques par dictionnaire</strong></em> : Utilisation de mots de passe courants contre des comptes connus.</li><li><em><strong>Attaques par tableaux arc-en-ciel</strong></em> (Rainbow table) : Pré-calcul des hashs pour des mots de passe courants et vérification de ces hashs dans les fichiers de mots de passe.</li></ol><h3 id="attaques-physiques" tabindex="-1">Attaques physiques : <a class="header-anchor" href="#attaques-physiques" aria-label="Permalink to &quot;Attaques physiques :&quot;">​</a></h3><ol><li>Utilisation de câbles malveillants ou de périphériques USB pour infecter les systèmes.</li><li>Clonage de cartes via des skimmers placés sur des lecteurs de carte.</li></ol><h3 id="attaques-par-intelligence-artificielle-adverse" tabindex="-1">Attaques par intelligence artificielle adverse : <a class="header-anchor" href="#attaques-par-intelligence-artificielle-adverse" aria-label="Permalink to &quot;Attaques par intelligence artificielle adverse :&quot;">​</a></h3><p>Altération des données d&#39;entraînement des algorithmes d&#39;apprentissage automatique ou attaques directes contre ces algorithmes.</p><h3 id="attaques-cryptographiques" tabindex="-1">Attaques cryptographiques : <a class="header-anchor" href="#attaques-cryptographiques" aria-label="Permalink to &quot;Attaques cryptographiques :&quot;">​</a></h3><ol><li><em><strong>Attaques par collision</strong></em> : Deux fichiers ayant la même valeur de hashage.</li><li><em><strong>Attaques par dégradation</strong></em> (Downgrade attack) : Baisse de la sophistication du chiffrement utilisé dans les communications, pouvant rendre le chiffrement plus vulnérable.</li></ol><h2 id="attaques-d-applications-objectif-1-3" tabindex="-1">Attaques d&#39;applications (Objectif 1.3) <a class="header-anchor" href="#attaques-d-applications-objectif-1-3" aria-label="Permalink to &quot;Attaques d&#39;applications (Objectif 1.3)&quot;">​</a></h2><p>L&#39;objectif 1.3 de l&#39;examen Security+ consiste à analyser les indicateurs potentiels associés aux attaques d&#39;applications.</p><ol><li><p><em><strong>Cross-Site Scripting</strong></em> (XSS) :</p><p>L&#39;attaquant manipule le navigateur web d&#39;un utilisateur pour exécuter du code JavaScript ou un autre langage de script. Les attaques de type persistant ou stocké sont courantes, où le code malveillant est inséré sur un site web et exécuté par les utilisateurs qui y accèdent.</p></li><li><p><em><strong>Injection</strong></em> :</p><p>L&#39;attaquant cherche des failles dans les sites web où il peut fournir des entrées, qui sont ensuite insérées dans une commande exécutée sur le système ou un autre serveur. L&#39;attaque par <strong>injection SQL</strong> est la plus courante, où l&#39;attaquant insère du code SQL malveillant pour manipuler la base de données.</p></li><li><p><em><strong>Transversalité de répertoire</strong></em> (Directory Traversal) :</p><p>L&#39;attaquant utilise des commandes de navigation de répertoire dans une URL pour accéder à des fichiers sensibles situés en dehors des parties destinées à l&#39;affichage sur le serveur.</p></li><li><p><em><strong>Débordement de tampon</strong></em> (Buffer Overflow) :</p><p>L&#39;attaquant injecte plus d&#39;informations que prévu dans un champ d&#39;entrée, provoquant un dépassement de mémoire tampon et permettant potentiellement l&#39;exécution de commandes malveillantes sur le serveur.</p></li></ol><h3 id="mitigation-des-attaques-d-applications" tabindex="-1">Mitigation des attaques d&#39;applications : <a class="header-anchor" href="#mitigation-des-attaques-d-applications" aria-label="Permalink to &quot;Mitigation des attaques d&#39;applications :&quot;">​</a></h3><p><em><strong>Validation des Entrées</strong></em> :</p><p>Assure que les données entrées correspondent au type attendu et ne contiennent pas de caractères malveillants. Par exemple, vérifier que les nombres entrés sont des entiers et qu&#39;ils se situent dans une plage valide.</p><p>En utilisant une validation des entrées robuste, de nombreux types d&#39;attaques d&#39;applications peuvent être atténués.</p><h2 id="attaques-reseau-objectif-1-4" tabindex="-1">Attaques réseau (Objectif 1.4) <a class="header-anchor" href="#attaques-reseau-objectif-1-4" aria-label="Permalink to &quot;Attaques réseau (Objectif 1.4)&quot;">​</a></h2><p>L&#39;objectif 1.4 de l&#39;examen Security+ consiste à analyser les indicateurs associés aux attaques réseau.</p><ol><li><p><em><strong>Attaques sans fil</strong></em> :</p><ol><li><em><strong>Faux point d&#39;accès</strong></em> (Evil Twin) : L&#39;attaquant crée un point d&#39;accès sans fil malveillant avec le même nom qu&#39;un point d&#39;accès légitime, afin de tromper les utilisateurs pour qu&#39;ils se connectent et divulguent leurs informations d&#39;identification.</li><li><em><strong>Attaque de désassociation</strong></em> (Disassociation) : L&#39;attaquant force un utilisateur à se déconnecter du réseau sans fil, puis capture des informations d&#39;authentification lors de la reconnexion.</li></ol></li><li><p><em><strong>Attaques de l&#39;homme du milieu</strong></em> (Man-in-the-Middle / On-Path) :</p><p>L&#39;attaquant se positionne entre l&#39;utilisateur et le serveur légitime, interceptant et manipulant les communications entre les deux parties.</p></li><li><p><em><strong>Attaques au niveau 2</strong></em> (Couche liaison de données OSI) :</p><ol><li><em><strong>Inondation de tables MAC</strong></em> (MAC Flooding) : L&#39;attaquant envoie un grand nombre de requêtes pour enregistrer différentes adresses MAC avec un commutateur réseau, entraînant un débordement de mémoire et permettant des attaques d&#39;écoute clandestine.</li><li><em><strong>Empoisonnement ARP</strong></em> (ARP Poisoning) : L&#39;attaquant injecte des informations MAC invalides dans un commutateur réseau, trompant les utilisateurs pour qu&#39;ils se connectent à des systèmes malveillants.</li></ol></li><li><p><em><strong>Attaques DNS</strong></em> :</p><ol><li><em><strong>Détournement de domaine</strong></em> (Domain Hijacking) : L&#39;attaquant vole un nom de domaine en piratant un compte chez le service où l&#39;entreprise l&#39;a enregistré, puis redirige le trafic vers son propre site.</li><li><em><strong>Empoisonnement DNS</strong></em> (DNS Poisoning) : L&#39;attaquant injecte des informations DNS invalides dans un serveur DNS, redirigeant les utilisateurs vers des sites malveillants.</li></ol></li><li><p><em><strong>Attaques par déni de service</strong></em> (DoS / DDoS) :</p><p><em><strong>Déni de service distribué</strong></em> (DDoS) : L&#39;attaquant inonde la victime de trafic provenant de nombreuses sources différentes, rendant difficile le blocage du trafic et rendant le système incapable de traiter le trafic légitime.</p></li></ol><p>Ces types d&#39;attaques réseau sont essentiels à connaître pour l&#39;examen Security+.</p><h2 id="acteurs-des-menaces-objectif-1-5" tabindex="-1">Acteurs des menaces (Objectif 1.5) <a class="header-anchor" href="#acteurs-des-menaces-objectif-1-5" aria-label="Permalink to &quot;Acteurs des menaces (Objectif 1.5)&quot;">​</a></h2><p>Dans l&#39;objectif 1.5 de l&#39;examen Security+, il est important de comprendre les différents acteurs des menaces, les vecteurs de menace et les sources de renseignements.</p><h3 id="acteurs-des-menaces" tabindex="-1">Acteurs des menaces : <a class="header-anchor" href="#acteurs-des-menaces" aria-label="Permalink to &quot;Acteurs des menaces :&quot;">​</a></h3><ol><li><em><strong>Menace interne</strong></em> : Des individus au sein de l&#39;organisation qui cherchent à compromettre la sécurité des systèmes pour diverses raisons.</li><li><em><strong>Menace externe</strong></em> : Des individus ou entités en dehors de l&#39;organisation qui tentent de s&#39;introduire dans les systèmes pour diverses raisons.</li><li><em><strong>Script kiddies</strong></em> : Des individus peu expérimentés qui utilisent des exploits développés par d&#39;autres pour mener des attaques sans grande sophistication.</li><li><em><strong>Advanced persistent threat</strong></em> (APT) : Des attaquants très déterminés et sophistiqués, souvent soutenus par des États-nations ou des groupes criminels, motivés par des objectifs politiques, militaires ou financiers.</li><li><em><strong>Hacktivistes</strong></em> : Des individus qui utilisent le piratage pour défendre une cause politique ou idéologique.</li></ol><h3 id="vecteurs-de-menace" tabindex="-1">Vecteurs de menace : <a class="header-anchor" href="#vecteurs-de-menace" aria-label="Permalink to &quot;Vecteurs de menace :&quot;">​</a></h3><ol><li><em><strong>Accès direct</strong></em> : Accès physique au système.</li><li><em><strong>Réseau</strong></em> : Utilisation des réseaux câblés ou sans fil pour mener des attaques.</li><li><em><strong>Courrier électronique</strong></em> : Utilisation de techniques de phishing et d&#39;ingénierie sociale pour compromettre les utilisateurs.</li><li><em><strong>Supports amovibles</strong></em> : Utilisation de périphériques de stockage amovibles pour introduire des menaces dans les systèmes.</li><li><em><strong>Fournisseurs</strong></em> : Introduction de menaces dans la chaîne d&#39;approvisionnement d&#39;une organisation en corrompant les fournisseurs ou les dispositifs.</li></ol><h3 id="sources-de-renseignements-sur-les-menaces" tabindex="-1">Sources de renseignements sur les menaces : <a class="header-anchor" href="#sources-de-renseignements-sur-les-menaces" aria-label="Permalink to &quot;Sources de renseignements sur les menaces :&quot;">​</a></h3><ol><li><em><strong>Open Source Intelligence</strong></em> (OSINT) : Informations accessibles au public.</li><li><em><strong>Closed Source Intelligence</strong></em> : Informations propriétaires achetées auprès de sources privées.</li><li><em><strong>Bases de données de vulnérabilités</strong></em> : Informations sur les failles de sécurité.</li><li><em><strong>Centres de partage d&#39;informations</strong></em> : Plateformes réunissant les secteurs public et privé pour partager des informations sur les menaces.</li><li><em><strong>Dark Web</strong></em> : Forums de piratage et autres sources sur le web sombre.</li></ol><p>Il est essentiel pour les équipes de cybersécurité de comprendre les évolutions du paysage des menaces et de recueillir des renseignements sur ceux-ci. Cela afin de concevoir des mesures de sécurité efficaces contre ces menaces en constante évolution.</p><h2 id="vulnerabilites-et-risques-associes-objectif-1-6" tabindex="-1">Vulnérabilités et risques associés (Objectif 1.6) <a class="header-anchor" href="#vulnerabilites-et-risques-associes-objectif-1-6" aria-label="Permalink to &quot;Vulnérabilités et risques associés (Objectif 1.6)&quot;">​</a></h2><p>Dans l&#39;objectif 1.6 de l&#39;examen Security+, il est crucial de comprendre les préoccupations en matière de sécurité associées à différents types de vulnérabilités, tant sur site que dans le cloud.</p><h3 id="vulnerabilites-courantes" tabindex="-1">Vulnérabilités courantes : <a class="header-anchor" href="#vulnerabilites-courantes" aria-label="Permalink to &quot;Vulnérabilités courantes :&quot;">​</a></h3><ol><li><em><strong>Zero-Day Vulnerability</strong></em> : Une vulnérabilité nouvellement découverte qui n&#39;a pas encore de correctif disponible, permettant aux attaquants de l&#39;exploiter avant que le fournisseur ne la corrige.</li><li><em><strong>Configurations faibles</strong></em> : Paramètres incorrects ou non sécurisés des systèmes et des logiciels, comme des autorisations ouvertes, des comptes administratifs non sécurisés, des erreurs affichant trop d&#39;informations, ou l&#39;utilisation de chiffrements faibles.</li><li><em><strong>Gestion de correctifs défectueuse</strong></em> : L&#39;absence de gestion efficace des correctifs, laissant les systèmes ouverts aux attaques connues depuis longtemps.</li><li><em><strong>Plateformes héritées</strong></em> : L&#39;utilisation de systèmes anciens non pris en charge par leur fabricant, exposant les organisations à des vulnérabilités connues mais non corrigées.</li><li><em><strong>Risques tiers</strong></em> : Les vulnérabilités introduites par des fournisseurs tiers, notamment les fournisseurs de matériel, de logiciels et de services cloud.</li></ol><h3 id="impact-des-vulnerabilites" tabindex="-1">Impact des vulnérabilités : <a class="header-anchor" href="#impact-des-vulnerabilites" aria-label="Permalink to &quot;Impact des vulnérabilités :&quot;">​</a></h3><ol><li><em><strong>Perte de données</strong></em> : Les failles de sécurité peuvent entraîner des violations de données et l&#39;exfiltration de données sensibles.</li><li><em><strong>Vol d&#39;identité</strong></em> : Les attaquants peuvent exploiter les vulnérabilités pour voler des identifiants et commettre des actes de fraude.</li><li><em><strong>Dommages financiers et réputationnels</strong></em> : Les attaques réussies peuvent causer des dommages financiers importants et ternir la réputation de l&#39;organisation.</li><li><em><strong>Interruption des activités</strong></em> : Les attaques entraînant une perte de disponibilité des systèmes peuvent perturber les opérations commerciales.</li></ol><h3 id="gestion-des-vulnerabilites" tabindex="-1">Gestion des vulnérabilités : <a class="header-anchor" href="#gestion-des-vulnerabilites" aria-label="Permalink to &quot;Gestion des vulnérabilités :&quot;">​</a></h3><p>Il est essentiel de mettre en place un programme solide de gestion des vulnérabilités pour identifier, évaluer et atténuer les vulnérabilités de manière proactive.</p><p>Cela implique la mise en œuvre de bonnes pratiques de gestion des correctifs, la surveillance des configurations système, et la gestion des fournisseurs pour réduire les risques associés aux vulnérabilités.</p><p>En comprenant ces vulnérabilités et en mettant en œuvre des mesures appropriées pour les gérer, les organisations peuvent réduire les risques liés à la sécurité et protéger efficacement leurs systèmes et leurs données.</p><h2 id="techniques-d-evaluation-de-la-securite-objectif-1-7" tabindex="-1">Techniques d&#39;évaluation de la sécurité (Objectif 1.7) <a class="header-anchor" href="#techniques-d-evaluation-de-la-securite-objectif-1-7" aria-label="Permalink to &quot;Techniques d&#39;évaluation de la sécurité (Objectif 1.7)&quot;">​</a></h2><p>L&#39;objectif 1.7 de l&#39;examen Security+ consiste à résumer les techniques utilisées dans les évaluations de sécurité, qui peuvent être divisées en quatre grandes catégories :</p><h3 id="balayage-de-vulnerabilites" tabindex="-1">Balayage de vulnérabilités : <a class="header-anchor" href="#balayage-de-vulnerabilites" aria-label="Permalink to &quot;Balayage de vulnérabilités :&quot;">​</a></h3><ol><li><em><strong>Ciblage</strong></em> : Les balayages de vulnérabilités peuvent cibler les réseaux, les applications ou les applications Web pour identifier les vulnérabilités.</li><li><em><strong>Types d&#39;accès</strong></em> : Les balayages peuvent être effectués avec ou sans privilèges d&#39;accès (scans avec ou sans identification).</li><li><em><strong>Intrusifs vs. non-intrusifs</strong></em> : Les balayages intrusifs peuvent perturber le système, tandis que les balayages non intrusifs limitent les risques de perturbation.</li></ol><h3 id="chasse-aux-menaces-threat-hunting" tabindex="-1">Chasse aux menaces (Threat Hunting) : <a class="header-anchor" href="#chasse-aux-menaces-threat-hunting" aria-label="Permalink to &quot;Chasse aux menaces (Threat Hunting) :&quot;">​</a></h3><p>Cette technique suppose qu&#39;un attaquant a déjà accédé au système, puis recherche activement les indicateurs de compromission à travers diverses sources d&#39;informations.</p><h3 id="analyse-des-journaux-log-analysis" tabindex="-1">Analyse des journaux (Log Analysis) : <a class="header-anchor" href="#analyse-des-journaux-log-analysis" aria-label="Permalink to &quot;Analyse des journaux (Log Analysis) :&quot;">​</a></h3><ol><li><em><strong>Les systèmes SIEM</strong></em> (Security Information and Event Management) agrègent et corrélaient les journaux de différents systèmes pour détecter les intrusions ou autres événements de sécurité.</li><li><em><strong>Les outils SOAR</strong></em> (Security Orchestration, Automation, and Response) automatisent les réponses aux événements détectés par un SIEM.</li></ol><h3 id="gestion-des-resultats-de-balayage" tabindex="-1">Gestion des résultats de balayage : <a class="header-anchor" href="#gestion-des-resultats-de-balayage" aria-label="Permalink to &quot;Gestion des résultats de balayage :&quot;">​</a></h3><p>Les résultats des balayages de vulnérabilités sont classés en vrais positifs (vulnérabilités réelles), faux positifs (fausses alertes), vrais négatifs (absence de vulnérabilités) et faux négatifs (vulnérabilités manquées).</p><p>Les vulnérabilités sont souvent notées selon leur gravité à l&#39;aide du Common Vulnerability Scoring System (CVSS).</p><p>En utilisant ces techniques, les équipes de sécurité peuvent identifier, évaluer et atténuer les risques de sécurité pour protéger efficacement les systèmes et les données de leur organisation.</p><h2 id="techniques-de-test-de-penetration" tabindex="-1">Techniques de test de pénétration : <a class="header-anchor" href="#techniques-de-test-de-penetration" aria-label="Permalink to &quot;Techniques de test de pénétration :&quot;">​</a></h2><p>L&#39;objectif 1.8 de l&#39;examen Security+ consiste à expliquer les techniques utilisées dans les tests de pénétration.</p><h3 id="types-de-tests" tabindex="-1">Types de tests : <a class="header-anchor" href="#types-de-tests" aria-label="Permalink to &quot;Types de tests :&quot;">​</a></h3><ol><li><em><strong>Black Box</strong></em> : Le testeur n&#39;a pas d&#39;informations internes sur les systèmes cibles, imitant ainsi une attaque réelle.</li><li><em><strong>Gray Box</strong></em> : Le testeur dispose de certaines informations, mais pas de la totalité de l&#39;environnement cible.</li><li><em><strong>White Box</strong></em> : Le testeur dispose de nombreuses informations sur les systèmes cibles, accélérant ainsi le test.</li></ol><h3 id="regles-d-engagement" tabindex="-1">Règles d&#39;engagement : <a class="header-anchor" href="#regles-d-engagement" aria-label="Permalink to &quot;Règles d&#39;engagement :&quot;">​</a></h3><p>Les règles d&#39;engagement doivent être définies avant le test pour déterminer ce que les testeurs sont autorisés à faire et les procédures à suivre en cas de découverte de vulnérabilités.</p><h3 id="methodes-utilisees" tabindex="-1">Méthodes utilisées : <a class="header-anchor" href="#methodes-utilisees" aria-label="Permalink to &quot;Méthodes utilisées :&quot;">​</a></h3><ol><li><em><strong>Reconnaissance</strong></em> : Les testeurs effectuent souvent une reconnaissance pour comprendre la structure de l&#39;organisation et les systèmes existants, utilisant à la fois des méthodes physiques et électroniques.</li><li><em><strong>Exploitation des vulnérabilités</strong></em> : Les testeurs tentent d&#39;exploiter des vulnérabilités réelles sur les systèmes cibles pour accéder au réseau et aux données sensibles.</li><li><em><strong>Escalade de privilèges</strong></em> : Une fois l&#39;accès initial obtenu, les testeurs tentent d&#39;obtenir des privilèges administratifs pour étendre leur contrôle sur le réseau.</li><li><em><strong>Établissement de persistence</strong></em> : Les testeurs mettent en place des moyens de réaccéder aux systèmes même après la fin du test.</li><li><em><strong>Nettoyage et restauration</strong></em> : À la fin du test, les testeurs nettoient et restaurent les systèmes à leur état d&#39;origine.</li></ol><h4 id="exercices-de-cybersecurite" tabindex="-1">Exercices de cybersécurité : <a class="header-anchor" href="#exercices-de-cybersecurite" aria-label="Permalink to &quot;Exercices de cybersécurité :&quot;">​</a></h4><p>Les exercices de cybersécurité mettent en scène des équipes de défense (blue team) et d&#39;attaque (red team), ainsi qu&#39;une équipe arbitre (white team) pour simuler des incidents réels. À la fin de l&#39;exercice, les équipes se réunissent en tant qu&#39;équipe violette (purple team) pour partager les connaissances et les leçons apprises.</p><p>En utilisant ces techniques, les équipes de test de pénétration peuvent identifier les vulnérabilités et renforcer la sécurité des systèmes de l&#39;organisation.</p><hr><br><div class="info custom-block"><p class="custom-block-title">Sources</p><p>Ce cours s&#39;inspire du cours sur Linkedin eLearning de <em><strong>Mike CHAPPLE</strong></em> :</p><p><a href="https://www.linkedin.com/learning/comptia-security-plus-sy0-601-cert-prep-1-threats-attacks-and-vulnerabilities?contextUrn=urn%3Ali%3AlyndaLearningPath%3A57bdd64992015ae4c0cb990e&amp;dApp=36524473&amp;leis=LAA&amp;u=98048682" target="_blank" rel="noreferrer">https://www.linkedin.com/learning/comptia-security-plus-sy0-601-cert-prep-1-threats-attacks-and-vulnerabilities?contextUrn=urn%3Ali%3AlyndaLearningPath%3A57bdd64992015ae4c0cb990e&amp;dApp=36524473&amp;leis=LAA&amp;u=98048682</a></p></div>',76);function m(p,g,h,f,b,q){const s=i("Badge");return l(),a("div",null,[d,n(s,{type:"tip",text:"Rédigé le 24/03/2024"}),c])}const x=t(u,[["render",m]]);export{y as __pageData,x as default};
