import{_ as r,c as n,j as s,a,G as i,an as l,B as o,o as u}from"./chunks/framework.ys1W0Hiz.js";const c="/assets/iceberg.SH2d9MzO.png",f=JSON.parse('{"title":"Dark Web","description":"","frontmatter":{"sidebar":"auto"},"headers":[],"relativePath":"cybersecurite/darkweb/intro.md","filePath":"cybersecurite/darkweb/intro.md","lastUpdated":1729429463000}'),d={name:"cybersecurite/darkweb/intro.md"};function p(m,e,b,g,h,v){const t=o("Badge");return u(),n("div",null,[e[0]||(e[0]=s("h1",{id:"dark-web",tabindex:"-1"},[a("Dark Web "),s("a",{class:"header-anchor",href:"#dark-web","aria-label":'Permalink to "Dark Web"'},"​")],-1)),i(t,{type:"tip",text:"Rédigé le 19/04/2024"}),e[1]||(e[1]=l('<p>Internet est divisé en trois sections : le <em><strong>Clear Web</strong></em>, le <em><strong>Deep Web</strong></em> ainsi que le <em><strong>Dark web</strong></em>.</p><p>Il est important de se rendre compte de son intérêt, et comment celui-ci peut être utilisé dans le cadre du renseignement, tant pour les forces de l&#39;ordre que pour les sociétés de renseignement sur les menaces.</p><p><img src="'+c+'" alt="Iceberg d&#39;Internet"></p><h2 id="le-clear-web" tabindex="-1">Le Clear Web <a class="header-anchor" href="#le-clear-web" aria-label="Permalink to &quot;Le Clear Web&quot;">​</a></h2><p>Cette partie est souvent appelée le <em>Web visible</em> ou le <em>Web indexé</em>. Elle est constituée de sites Web et de pages Web qui sont accessibles au public et qui sont indexés par les moteurs de recherche. Ces sites comprennent des plateformes populaires comme Facebook, Google, YouTube et Reddit, ainsi que de nombreux autres sites que les gens visitent quotidiennement.</p><p>Lorsqu&#39;une personne effectue une recherche sur Google, par exemple, elle obtient des résultats provenant de ce Web visible, car les robots d&#39;exploration de Google ont indexé ces pages et peuvent les présenter comme résultats de recherche. Les robots d&#39;exploration sont des programmes informatiques qui parcourent le Web, suivent les liens entre les pages et indexent le contenu pour permettre aux moteurs de recherche de fournir des résultats pertinents aux utilisateurs.</p><p>Si un site autorise les robots d&#39;exploration à indexer ses pages, cela signifie qu&#39;il est plus susceptible d&#39;apparaître dans les résultats de recherche lorsque les utilisateurs recherchent des termes pertinents. C&#39;est une partie essentielle du référencement et de la visibilité en ligne pour de nombreux sites Web.</p><p>Le Clear Web contient des sites accessibles au public, conçus pour être utilisés sans avoir besoin de méthodes d&#39;accès spéciales comme des clés ou des listes blanches d&#39;adresses IP. Ces sites sont généralement disponibles sur la couche <strong>Surface du Web</strong>, où les utilisateurs peuvent les trouver et y accéder relativement facilement.</p><p>Les sites juridiques font partie de cette catégorie, offrant des informations et des services juridiques destinés au grand public. Ils ne nécessitent généralement pas de procédures d&#39;accès complexes et peuvent être consultés par quiconque a accès à Internet, bien que certains d&#39;entre eux puissent exiger un enregistrement pour accéder à certaines fonctionnalités ou contenus.</p><h2 id="le-deep-web" tabindex="-1">Le Deep Web <a class="header-anchor" href="#le-deep-web" aria-label="Permalink to &quot;Le Deep Web&quot;">​</a></h2><p>Le Deep Web, aussi connu sous le nom de <em>Web souterrain</em> ou <em>Web invisible</em>, représente la partie d&#39;Internet qui <strong>n&#39;est pas indexée par les moteurs de recherche</strong> classiques comme Google ou Bing. Cette section du Web est immense et comprend une grande quantité de données et de pages qui ne sont pas accessibles au public de manière conventionnelle.</p><p>Les propriétaires de sites du Deep Web peuvent avoir <strong>empêché l&#39;indexation</strong> de leurs pages par les robots d&#39;exploration, ou bien ils peuvent avoir mis en place des contrôles d&#39;accès restreints, ne permettant l&#39;accès qu&#39;à un public spécifique. Contrairement au Web visible, le Deep Web n&#39;est pas facilement accessible par une simple recherche sur Google. Les informations qu&#39;il <strong>contient sont souvent des données sensibles ou privées</strong>, telles que des comptes utilisateurs sur des plateformes comme Amazon ou Facebook, des forums privés, des pages d&#39;adhésion restreintes ou des services bancaires en ligne.</p><p>Il est important de noter que <em><strong>le Dark Web et le Deep Web sont des termes distincts</strong></em>. Bien que souvent confondus, le Dark Web se réfère à une partie spécifique du Deep Web qui est intentionnellement cachée et où les activités illicites sont souvent associées. Le Deep Web, en revanche, englobe toutes les données et pages non indexées, qu&#39;elles soient légitimes ou non. En résumé, le Deep Web peut inclure des informations privées et sensibles qui ne sont pas destinées à être publiques, mais il ne se limite pas nécessairement à des activités illégales.</p><h2 id="le-dark-web" tabindex="-1">Le Dark Web <a class="header-anchor" href="#le-dark-web" aria-label="Permalink to &quot;Le Dark Web&quot;">​</a></h2><p>Le Dark Web est une section distincte et souvent mal comprise d&#39;Internet, utilisée pour diverses activités, y compris l&#39;anonymat, les opérations illégales et la collecte de renseignements sensibles.</p><p>L&#39;accès au Dark Web nécessite généralement l&#39;utilisation de logiciels spéciaux comme <em><strong>Tor</strong></em> (<em><strong>T</strong></em>he <em><strong>O</strong></em>nion <em><strong>R</strong></em>outer), qui permettent aux utilisateurs de naviguer de manière anonyme en masquant leur adresse IP. Les sites du Dark Web utilisent des <strong>domaines de premier niveau</strong> (TLD) spécifiques, tels que <code>.onion</code>, plutôt que les TLD couramment utilisés sur le Web visible.</p><p>Quant à la légalité de visiter le Dark Web, cela dépend principalement des activités que l&#39;on peut y mener. En soi, <em>visiter le Dark Web n&#39;est pas illégal</em> dans la plupart des pays, mais il est important de noter que de <strong>nombreuses activités qui s&#39;y déroulent sont illégales</strong>, telles que la vente de drogues, d&#39;armes et d&#39;autres biens illicites, ainsi que la distribution de contenu répréhensible comme la pornographie infantile.</p><p>Par conséquent, bien que l&#39;accès au Dark Web puisse être légal, l&#39;engagement dans des activités illégales sur cette plateforme <em>peut entraîner des conséquences juridiques graves</em>. Il est donc essentiel pour les utilisateurs de comprendre les risques associés à l&#39;utilisation du Dark Web et de <strong>veiller à respecter les lois</strong> en vigueur dans leur juridiction. Une sensibilisation aux dangers potentiels et la prise de mesures pour rester en sécurité en ligne sont également cruciales lors de l&#39;exploration de cette partie d&#39;Internet.</p><h2 id="activitees-associees" tabindex="-1">Activitées associées <a class="header-anchor" href="#activitees-associees" aria-label="Permalink to &quot;Activitées associées&quot;">​</a></h2><p>L&#39;accès au Dark Web est généralement limité et peu recommandé pour la plupart des utilisateurs, mais certains professionnels de la sécurité informatique peuvent être amenés à y accéder dans le cadre de leur travail.</p><h3 id="analyste-des-menaces" tabindex="-1">Analyste des menaces <a class="header-anchor" href="#analyste-des-menaces" aria-label="Permalink to &quot;Analyste des menaces&quot;">​</a></h3><p>Les analystes des menaces surveillent les activités potentiellement dangereuses sur le Dark Web, telles que la planification d&#39;attaques ou la vente d&#39;informations confidentielles. Ils peuvent infiltrer des forums ou des sites privés pour recueillir des informations et protéger leur entreprise contre les menaces émergentes.</p><h3 id="analyste-de-programmes-malveillants" tabindex="-1">Analyste de programmes malveillants <a class="header-anchor" href="#analyste-de-programmes-malveillants" aria-label="Permalink to &quot;Analyste de programmes malveillants&quot;">​</a></h3><p>Les analystes de programmes malveillants recherchent des échantillons de logiciels malveillants sur le Dark Web afin de les analyser et de développer des contre-mesures pour protéger leur organisation contre les attaques. Ils surveillent également les places de marché où des malwares sont vendus et étudient les tendances pour anticiper les nouvelles menaces.</p><h3 id="chercheur-en-securite" tabindex="-1">Chercheur en sécurité <a class="header-anchor" href="#chercheur-en-securite" aria-label="Permalink to &quot;Chercheur en sécurité&quot;">​</a></h3><p>Les chercheurs en sécurité explorent le Dark Web pour détecter et analyser les activités malveillantes, telles que la vente de vulnérabilités Zero Day ou l&#39;échange d&#39;informations sur les attaques. Leur travail consiste à identifier les nouvelles menaces et à partager leurs découvertes avec la communauté de la sécurité informatique.</p><h3 id="forces-de-l-ordre" tabindex="-1">Forces de l&#39;ordre <a class="header-anchor" href="#forces-de-l-ordre" aria-label="Permalink to &quot;Forces de l&#39;ordre&quot;">​</a></h3><p>Les organismes chargés de l&#39;application de la loi utilisent le Dark Web pour enquêter sur des activités criminelles telles que le trafic de drogue, la vente d&#39;armes illicites et la cybercriminalité. Ils peuvent infiltrer des réseaux criminels, recueillir des preuves et prendre des mesures pour arrêter les personnes impliquées dans des activités illégales.</p><hr><br><div class="info custom-block"><p class="custom-block-title">Sources</p><p>Ce cours s&#39;inspire de Security Blue Team du cours &quot;<em>Introduction to Dark Web Operations</em>&quot; : <a href="https://elearning.securityblue.team/home/courses/free-courses/introduction-to-dark-web-operations#description" target="_blank" rel="noreferrer">https://elearning.securityblue.team/home/courses/free-courses/introduction-to-dark-web-operations#description</a></p></div>',31))])}const W=r(d,[["render",p]]);export{f as __pageData,W as default};
